INSERT INTO `posts` (`id`, `title`, `slug_title`, `content`, `summary`, `images`, `hot`, `trending`, `active`, `type_id`, `created_at`, `updated_at`) VALUES
(13, 'Nhà Thép Tiền Chế', 'nha-thep-tien-che', '<h1><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; Tiền Chế L&agrave; G&igrave;? C&aacute;ch L&agrave;m Nh&agrave; Tiền Chế Bằng Khung Th&eacute;p Rẻ V&agrave; Đẹp</span></span></h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"http://www.hoangphatdanang.com/uploads/image/images/nha-container-xu-huong-phat-trien-tai-viet-nam-1-800x450(1).jpg\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; th&eacute;p tiền chế với những ưu điểm như trọng lượng nhẹ, dễ thiết kế, thi c&ocirc;ng nhanh gi&uacute;p tiết kiệm được chi ph&iacute; thi c&ocirc;ng cũng như giảm thời gian thi c&ocirc;ng n&ecirc;n ng&agrave;y c&agrave;ng được lựa chọn v&agrave; sử dụng nhiều trong những c&ocirc;ng tr&igrave;nh d&acirc;n dụng hay nh&agrave; xưởng thay cho c&aacute;c sản phẩm nh&agrave; b&ecirc; t&ocirc;ng truyền thống.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&agrave;i viết dưới đ&acirc;y sẽ cung cấp cho bạn những th&ocirc;ng tin đầy đủ nhất về nh&agrave; tiền chế như&nbsp;nh&agrave; th&eacute;p tiền chế l&agrave; g&igrave;? Ưu điểm ra sao?&hellip;&hellip;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"http://www.hoangphatdanang.com/uploads/image/images/4(3).jpg\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Nh&agrave; th&eacute;p tiền chế l&agrave; g&igrave;?</strong></span></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; th&eacute;p tiền chế (hay c&ograve;n gọi l&agrave; nh&agrave; tiền chế) l&agrave; loại nh&agrave; được x&acirc;y dựng với khung trụ l&agrave; vật liệu bằng th&eacute;p v&agrave; được lắp đặt dựa theo bản vẽ kiến tr&uacute;c kỹ thuật. Một nh&agrave; th&eacute;p tiền chế ho&agrave;n chỉnh được tạo ra qua 3 giai đoạn:</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">-Một l&agrave;, thiết kế.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">-Hai l&agrave;, gia c&ocirc;ng cấu kiện.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">-Ba l&agrave;, lắp dựng lại c&ocirc;ng tr&igrave;nh.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">To&agrave;n bộ kết cấu của nh&agrave; th&eacute;p tiền chế đều được sản xuất sẵn n&ecirc;n việc lắp dựng được diễn ra rất nhanh ch&oacute;ng. Những c&ocirc;ng tr&igrave;nh thường sử dụng loại nh&agrave; n&agrave;y c&oacute; thể kể đến như: nh&agrave; kho, nh&agrave; xưởng, si&ecirc;u thị, nh&agrave; trưng b&agrave;y, nh&agrave; cao tầng, c&ocirc;ng tr&igrave;nh thương mại&hellip;</span></span></p>\r\n\r\n<p dir=\"ltr\">&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Cấu tạo của nh&agrave; th&eacute;p tiền chế</strong></span></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Nhà Tiền Chế Là Gì? Cách Làm Nhà Tiền Chế Bằng Khung Thép Rẻ Và Đẹp 1\" src=\"https://scontent-hkt1-1.xx.fbcdn.net/v/t1.0-9/89956724_3159579150721262_409681193605267456_n.png?_nc_cat=103&amp;_nc_sid=07e735&amp;_nc_ohc=FUMEz56mAZ0AX-MeClp&amp;_nc_ht=scontent-hkt1-1.xx&amp;oh=4ac38891b4decc336c22a27c26854e5f&amp;oe=5E9252C0\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Kết cấu ch&iacute;nh của Nh&agrave; Tiền Chế</strong></span></span></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tương tự nh&agrave; b&ecirc; t&ocirc;ng,&nbsp;<strong>nh&agrave; tiền chế</strong>&nbsp;vẫn cần bộ phận m&oacute;ng để chịu lực cho ng&ocirc;i nh&agrave;. Người ta c&oacute; thể l&agrave;m m&oacute;ng n&ocirc;ng hoặc m&oacute;ng s&acirc;u t&ugrave;y v&agrave;o nhu cầu. Với những c&ocirc;ng tr&igrave;nh lớn sẽ y&ecirc;u cầu l&agrave;m m&oacute;ng s&acirc;u chống lật. Ngo&agrave;i phần m&oacute;ng, kết cấu ch&iacute;nh c&ograve;n c&oacute;: kết cấu mang lực m&aacute;i, dầm cầu chạy, hệ khung chống gi&oacute;, hệ giằng, cột, k&egrave;o h&igrave;nh chữ &ldquo;I&rdquo; để l&agrave;m khung ch&iacute;nh,&hellip; Đ&acirc;y l&agrave; những cấu tạo quan trọng chịu to&agrave;n bộ tải trọng của&nbsp;<strong>nh&agrave; tiền chế</strong>.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Kết cấu phụ của Nh&agrave; Tiền Chế</strong></span></span></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c kết cấu phụ của&nbsp;<strong>nh&agrave; tiền chế</strong>&nbsp;gồm: v&aacute;ch ngăn, hệ khung đỡ v&aacute;ch ngăn, cầu thang v&agrave; x&agrave; gồ m&aacute;i, hệ s&agrave;n c&ocirc;ng t&aacute;c, x&agrave; gồ tường h&igrave;nh chữ &ldquo;Z&rdquo; v&agrave; &ldquo;C&rdquo;,&hellip; Tuy l&agrave; kết cấu phụ nhưng những cấu tạo n&agrave;y g&oacute;p phần kh&ocirc;ng nhỏ v&agrave;o việc x&acirc;y dựng v&agrave; ho&agrave;n thiện c&ocirc;ng tr&igrave;nh.</span></span></p>\r\n\r\n<h3><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Kết cấu bao che, tạo h&igrave;nh của Nh&agrave; Tiền Chế</strong></span></span></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; phần quan trọng kh&ocirc;ng k&eacute;m để cấu tạo n&ecirc;n&nbsp;<strong>nh&agrave; tiền chế</strong>. Bởi lẽ để c&oacute; một c&ocirc;ng tr&igrave;nh&nbsp;ho&agrave;n thiện, kh&ocirc;ng thể thiếu phần bao che, tạo h&igrave;nh từ c&aacute;c tấm vật liệu sẵn c&oacute; như: t&ocirc;n lợp m&aacute;i, tấm xi măng tạo h&igrave;nh bao che nội ngoại thất,&nbsp;tấm l&oacute;t s&agrave;n xi măng,&nbsp;tấm l&oacute;t s&agrave;n cemboard, tấm th&eacute;p,&hellip; nhằm giới hạn kh&ocirc;ng gian v&agrave; bảo vệ nh&agrave; khỏi c&aacute;c yếu tố t&aacute;c động từ m&ocirc;i trường b&ecirc;n ngo&agrave;i. Đồng thời, yếu tố thẩm mỹ được quyết định bởi c&aacute;c tấm bao che v&agrave; tạo h&igrave;nh n&agrave;y.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Những ưu điểm của nh&agrave; th&eacute;p tiền chế</strong></span></span></h2>\r\n\r\n<p dir=\"ltr\">&nbsp;</p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; Th&eacute;p Tiền Chế &ndash;&nbsp;Gi&uacute;p chủ đầu tư tiết kiệm vật liệu phụ.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; th&eacute;p tiền chế c&oacute; khả năng chịu lực tốt c&ugrave;ng trọng lượng nhẹ hơn so với những vật liệu kh&aacute;c n&ecirc;n sẽ gi&uacute;p giảm bớt tải trọng khi x&acirc;y dựng.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc lắp đặt nh&agrave; khung th&eacute;p diễn ra nhanh ch&oacute;ng v&agrave; đơn giản hơn. Th&aacute;ch thức mọi điều kiện thời tiết.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; khung th&eacute;p tiền chế cũng gi&uacute;p tận dụng được tối đa kh&ocirc;ng gian của nh&agrave; xưởng, dễ mở rộng quy m&ocirc; khi cần.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">So với nh&agrave; b&ecirc; t&ocirc;ng truyền thống, nh&agrave; tiền chế gi&aacute; rẻ hơn, gi&uacute;p tiết kiệm thời gian v&agrave; tiền bạc cho doanh nghiệp.</span></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; Th&eacute;p Tiền Chế &ndash;&nbsp;C&oacute; t&iacute;nh đồng bộ cao trong x&acirc;y dựng.</span></span></p>', 'dfgfdgdfg', '921.jpg', '1', 1, '1', 1, '2022-03-23 01:11:05', '2022-03-23 01:11:05'),
(14, 'Cấu Tạo Nhà Khung Máy Thép Tiền Chế', 'cau-tao-nha-khung-may-thep-tien-che', '<ul>\r\n	<li><span style=\"font-size:18px\">Việc thi c&ocirc;ng lắp dựng, li&ecirc;n kết cột th&eacute;p&nbsp;v&agrave; dầm th&eacute;p, li&ecirc;n kết dầm th&eacute;p với cột b&ecirc; t&ocirc;ng&nbsp;đối với l&agrave;m nh&agrave; tiền chế&nbsp;d&acirc;n dụng khung th&eacute;p&nbsp;diễn ra phức tạp, đ&ograve;i hỏi c&ocirc;ng t&aacute;c tổ chức chuy&ecirc;n m&ocirc;n ho&aacute; rất cao.</span></li>\r\n	<li><span style=\"font-size:18px\">Với nhiều đặc điểm địa h&igrave;nh phức tạp phải triển khai thi c&ocirc;ng bằng tay (l&acirc;u hơn, k&eacute;m an to&agrave;n hơn, tốn nhiều chi ph&iacute; vận chuyển, g&aacute; lắp).</span></li>\r\n	<li><span style=\"font-size:18px\">Trong c&aacute;ch l&agrave;m&nbsp;<strong>nh&agrave; th&eacute;p</strong>&nbsp;tiền chế th&igrave; song song với tiến độ thi c&ocirc;ng l&agrave; c&ocirc;ng t&aacute;c an to&agrave;n lao động. Th&ocirc;ng thường cấu tạo chi tiết phụ k&egrave;m theo vị tr&iacute; g&aacute; thang leo, neo d&acirc;y an to&agrave;, lưới an to&agrave;n trực tiếp v&agrave;o hệ kết cấu ch&iacute;nh.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\">Với nh&agrave; th&eacute;p d&acirc;n dung cao tầng: lu&ocirc;n c&oacute; thang m&aacute;y. L&uacute;c n&agrave;y tuỳ thuộc hệ kết cấu từng c&ocirc;ng tr&igrave;nh, tiến h&agrave;nh thiết kế l&otilde;i cứng cho c&ocirc;ng tr&igrave;nh th&ocirc;ng qua v&aacute;ch thang m&aacute;y.</span></p>\r\n\r\n<h4><span style=\"font-size:18px\"><strong>S&agrave;n, tường, v&aacute;ch ngăn nh&agrave; khung th&eacute;p d&acirc;n dụng:</strong></span></h4>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"sàn nhà khung thép dân dụng\" src=\"https://nhakhungthep.vn/upload_images/images/san-nha-thep-tien-che.jpeg\" title=\"sàn nhà khung thép dân dụng\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">S&agrave;n nh&agrave; th&eacute;p d&acirc;n dụng&nbsp;hiện tại rất đa dạng v&agrave; phong ph&uacute;, c&oacute; thể sử dụng bảng so s&aacute;nh sau để đ&aacute;nh gi&aacute;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tường, v&aacute;ch nh&agrave; kết cấu th&eacute;p hiện tại rất đa dạng v&agrave; phong ph&uacute;, c&oacute; thể sử dụng bảng so s&aacute;nh sau để đ&aacute;nh gi&aacute;&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Loại s&agrave;n</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Chiều dầy</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Trọng lượng</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Thi c&ocirc;ng</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Ph&acirc;n t&iacute;ch đặc điểm</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">S&agrave;n b&ecirc; t&ocirc;ng truyền thống</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">12 cm</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">200kg/m2</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Sử dụng coppha l&oacute;t như b&igrave;nh thường. Sau đ&oacute; thi c&ocirc;ng rải sắt s&agrave;n , lắp đặt đinh h&agrave;n mặt dầm , tiến h&agrave;nh đổ b&ecirc; t&ocirc;ng.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- S&agrave;n n&agrave;y rất phổ biến CT BTCT truyền thống. Đặc , chắc, đầm ch&acirc;n song thi c&ocirc;ng l&acirc;u , mất c&ocirc;ng gh&eacute;p, dỡ coppha . Hệ số tin cậy cao.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\"><a href=\"https://nhakhungthep.vn/nha-khung-thep-va-san-deck-ad36.html\">S&agrave;n desk</a></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">T&ocirc;n deking dầy 5-7 cm , s&agrave;n đổ xong dầy 8-10 cm.</span></p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">140kg/m2</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- T&ocirc;n Deking thay cho coppha trong qu&aacute; tr&igrave;nh thi c&ocirc;ng . N&ecirc;n chỉ phải rải decking , li&ecirc;n kết với dầm th&eacute;p bằng đinh h&agrave;n . Rải th&eacute;p s&agrave;n D6-8 a150 , h&agrave;n t&ocirc;n qu&acirc;y s&agrave;n l&agrave; c&oacute; thể đổ b&ecirc; t&ocirc;ng.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- S&agrave;n n&agrave;y rất phổ biến TC nh&agrave; khung th&eacute;p d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp . Đặc , chắc, đầm ch&acirc;n song thi c&ocirc;ng nhanh ch&oacute;ng. Hệ số tin cậy rất cao.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">S&agrave;n Cemboard</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">7cm bao gồm 5 cm xương th&eacute;p +2 cm tấm Cemboard.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">40kg/m2</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Sau khi gia c&ocirc;ng xong xương đỡ tấm 50x50x1,4mm a 40,7 cm , tiến h&agrave;nh lắp đặt tấm Cemboard 2 cm l&ecirc;n tr&ecirc;n bằng v&iacute;t. Sau đ&oacute; c&oacute; thể sử dụng được lu&ocirc;n hoặc l&aacute;t gạch, s&agrave;n gỗ.</span></p>\r\n\r\n			<p><span style=\"font-size:18px\">- Ngo&agrave;i ra c&ograve;n c&oacute; thể sử dụng vật liệu Foam pu c&aacute;ch &acirc;m, c&aacute;ch nhiệt cho s&agrave;n khiến s&agrave;n Cemboard đặc chắc đầm ch&acirc;n.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- S&agrave;n n&agrave;y c&oacute; mặt tr&ecirc;n thị trường 10 năm với nhiều ưu điểm như: nhẹ , thi c&ocirc;ng nhanh , chịu lực tốt. Song g&acirc;y tiếng ồn , c&oacute; độ rung tức thời do tấm s&agrave;n mỏng 2cm. Hệ số tin cậy cao.</span></p>\r\n\r\n			<p><span style=\"font-size:18px\">- Ngo&agrave;i ra c&ograve;n c&oacute; thể sử dụng vật liệu Foam pu c&aacute;ch &acirc;m, c&aacute;ch nhiệt cho s&agrave;n khiến s&agrave;n Cemboard đặc chắc đầm ch&acirc;n.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Ghi ch&uacute;</span></p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Tham khảo ngay : Ứng dụng Foam pu c&aacute;ch &acirc;m , c&aacute;ch nhiệt , chống ch&aacute;y trong nh&agrave; khung th&eacute;p .</span></p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Loại tường</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Chiều dầy</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Trọng lượng</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Thi c&ocirc;ng</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Ph&acirc;n t&iacute;ch đặc điểm</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Tường gạch truyền thống</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">10 cm</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">180 kg/m2</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Sử dụng gạch nung nh&agrave; m&aacute;y x&acirc;y tường . Song c&ocirc;ng t&aacute;c xử l&yacute; c&aacute;c vị tr&iacute; gi&aacute;p lai tiến h&agrave;nh phức tạp : R&acirc;u sắt, lưới th&eacute;p, b&ecirc; t&ocirc;ng ch&acirc;n tường, giằng tường.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Việc thi c&ocirc;ng x&acirc;y tường kh&ocirc;ng cơ giới ho&aacute; cao, song khi thi c&ocirc;ng xong sẽ cho 1 bức tường đặc chắc, an to&agrave;n. Độ cứng tổng thể c&ocirc;ng tr&igrave;nh cao hơn nhiều . Hệ số tin cậy rất cao.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Tường gạch nhẹ chưng &aacute;p</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">10 - 20 cm</span></p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">100 - 200 kg/m2</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Sử dụng gạch kh&iacute; chưng &aacute;p nh&agrave; m&aacute;y x&acirc;y tường . Song c&ocirc;ng t&aacute;c xử l&yacute; c&aacute;c vị tr&iacute; gi&aacute;p lai tiến h&agrave;nh phức tạp : R&acirc;u sắt, lưới th&eacute;p, b&ecirc; t&ocirc;ng ch&acirc;n tường, giằng tường.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Việc thi c&ocirc;ng x&acirc;y tường kh&ocirc;ng cơ giới ho&aacute; cao , song khi thi c&ocirc;ng xong sẽ cho 1 bức tường đặc chắc ở mức trung b&igrave;nh, an to&agrave;n. Độ cứng tổng thể c&ocirc;ng tr&igrave;nh cao hơn. Hệ số tin cậy cao.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">V&aacute;ch ngăn Cemboard</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">7cm bao gồm 5 cm xương th&eacute;p +2x8mm tấm Cemboard.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">40kg/m2</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">-Sau khi gia c&ocirc;ng xong xương v&aacute;ch 50x50x1,4mm a 40,7 cm, tiến h&agrave;nh lắp đặt tấm Cemboard 8 cmx2 mặt l&ecirc;n tr&ecirc;n bằng v&iacute;t. Sau đ&oacute; c&oacute; thể sử dụng được lu&ocirc;n ốp , sơn , d&aacute;n tường , hoặc ho&agrave;n thiện vật liệu kh&aacute;c l&ecirc;n tr&ecirc;n.&nbsp;</span></p>\r\n\r\n			<p><span style=\"font-size:18px\">Ngo&agrave;i ra để c&aacute;ch &acirc;m , c&aacute;ch nhiệt c&oacute; thể sử dụng th&ecirc;m b&ocirc;ng thuỷ tinh, hoặc bơm Foam pu c&aacute;ch &acirc;m, c&aacute;ch nhiệt, chống ch&aacute;y&nbsp;to&agrave;n khối.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- V&aacute;ch n&agrave;y c&oacute; mặt tr&ecirc;n thị trường 10 năm với nhiều ưu điểm như: nhẹ , thi c&ocirc;ng nhanh , chịu lực tốt song hay bị nứt . Ph&ugrave; hợp ốp tường WC, hoặc giấy d&aacute;n tường, v&aacute;ch ngăn nhẹ trong nh&agrave;. Hệ số tin cậy cao.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">V&aacute;ch ngăn t&ocirc;n PANEL</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">10 cm</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">5kg/m2</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Thi c&ocirc;ng lắp đặt trực tiếp bằng v&iacute;t, r&atilde;nh &acirc;m dương. Kh&ocirc;ng phải xử l&yacute; bề mặt c&oacute; thể sử dụng lu&ocirc;n.</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">- Ph&ugrave; hợp với nh&agrave; xưởng c&ocirc;ng nghiệp, kho lạnh, hoặc c&ocirc;ng tr&igrave;nh mang t&iacute;nh tạm thời (Số lượng tr&ecirc;n 150 m2 mới c&oacute; h&agrave;ng)</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">Ghi ch&uacute;</span></p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:18px\">​</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">Cột thu s&eacute;t: T&aacute;c dụng thu s&eacute;t v&agrave; qua hệ thống tiếp địa đưa xuống đất, đảm bảo an to&agrave;n cho sự an to&agrave;n cho m&aacute;y m&oacute;c v&agrave; c&aacute;c thiết bị điện tử khi bị s&eacute;t đ&aacute;nh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">X&acirc;y&nbsp;<em><strong>nh&agrave;&nbsp;</strong></em><em><strong>th&eacute;p</strong></em>&nbsp;tiền chế cao tầng: Bắt buộc sử dụng hệ s&agrave;n t&ocirc;n Deck đổ b&ecirc; t&ocirc;ng to&agrave;n khối.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"khung nhà thép tiền chế dân dụng cao tầng\" src=\"https://nhakhungthep.vn/upload_images/images/khung-nha-xuong-tien-che.jpeg\" title=\"khung nhà thép tiền chế dân dụng cao tầng\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:18px\">&gt;&gt; Xem ngay loạt b&agrave;i&nbsp;<a href=\"https://nhakhungthep.vn/nha-khung-thep-cao-tang---ban-san-sang-chua--ad45.html\" target=\"_blank\">Nh&agrave; khung th&eacute;p cao tầng tr&ecirc;n thế giới</a>&nbsp; trước khi quyết định&nbsp;&aacute;p dụng cho nh&agrave; bạn !</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"nhà khung thép dân dụng cao tầng\" src=\"https://nhakhungthep.vn/upload_images/images/nha-tien-che-cao-tang.jpeg\" title=\"nhà khung thép dân dụng cao tầng\" /></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Nh&agrave; th&eacute;p tiền chế - Nh&agrave; tiền chế</strong></span></h2>\r\n\r\n<p><span style=\"font-size:18px\">Như nội dung tr&ecirc;n ch&uacute;ng ta đ&atilde; t&igrave;m hiểu về nh&agrave; khung th&eacute;p d&acirc;n dụng. L&agrave; loại c&ocirc;ng tr&igrave;nh được ứng dụng phương ph&aacute;p x&acirc;y&nbsp;<strong>nh&agrave; tiền chế</strong>&nbsp;hay c&ograve;n gọi l&agrave;&nbsp;<strong>nh&agrave; th&eacute;p tiền chế</strong>.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Nh&agrave; th&eacute;p tiền chế l&agrave; g&igrave;?</strong></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; th&eacute;p tiền chế (nh&agrave; tiền chế) l&agrave; một dạng c&ocirc;ng tr&igrave;nh x&acirc;y dựng sử dụng bằng kết cấu th&eacute;p. C&aacute;c cấu kiện th&eacute;p được lắp gh&eacute;p với nhau theo bản vẽ&nbsp;thiết kế v&agrave; h&agrave;ng loạt c&aacute;c t&iacute;nh to&aacute;n về chịu&nbsp;lực, c&acirc;n bằng lực.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; th&eacute;p tiền chế được ứng dụng v&agrave;&nbsp;thi c&ocirc;ng cho cả c&ocirc;ng tr&igrave;nh d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp, từ nh&agrave; thấp tầng cho đến nh&agrave; cao tầng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">C&aacute;c giai đoạn để thực hiện x&acirc;y dựng nh&agrave; th&eacute;p tiền chế bao gồm c&aacute;c c&ocirc;ng đoạn ch&iacute;nh như:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Thiết kế tổng thể v&agrave; chi tiết nh&agrave; tiền chế.</span></li>\r\n	<li><span style=\"font-size:18px\">Sản xuất cấu kiện, khung th&eacute;p v&agrave; c&aacute;c phụ kiện.</span></li>\r\n	<li><span style=\"font-size:18px\">Lắp dựng v&agrave; ho&agrave;n thiện c&ocirc;ng tr&igrave;nh.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Đặc điểm của nh&agrave; th&eacute;p tiền chế</strong></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Một trong những đặc điểm nổi bật v&agrave; cũng l&agrave; ưu điểm của nh&agrave; tiền chế l&agrave; t&iacute;nh linh hoạt v&agrave; nhanh ch&oacute;ng. C&ocirc;ng tr&igrave;nh x&acirc;y dựng được thực hiện với thời gian ngắn do qu&aacute; tr&igrave;nh lắp dựng dựa tr&ecirc;n cấu kiện,&nbsp;<strong>khung th&eacute;p</strong>&nbsp;được sản xuất từ trước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Những loại c&ocirc;ng tr&igrave;nh được sử dụng phổ biến c&oacute; thể kể đến như:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Nh&agrave; xưởng, nh&agrave; m&aacute;y, kho b&atilde;i</span></li>\r\n	<li><span style=\"font-size:18px\">Bệnh viện v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh d&atilde; chiến</span></li>\r\n	<li><span style=\"font-size:18px\">Si&ecirc;u thị, chợ,...</span></li>\r\n	<li><span style=\"font-size:18px\">B&atilde;i đỗ&nbsp;xe nhiều tầng,...</span></li>\r\n</ul>\r\n\r\n<h4><span style=\"font-size:18px\"><strong>Ưu điểm của nh&agrave; th&eacute;p tiền chế</strong></span></h4>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Dễ d&agrave;ng lắp dựng v&agrave; th&aacute;o bỏ, di rời</span></li>\r\n	<li><span style=\"font-size:18px\">Trọng lượng thấp hơn với nh&agrave; b&ecirc; t&ocirc;ng cốt th&eacute;p, gi&uacute;p dễ d&agrave;ng trong qu&aacute; tr&igrave;nh vận chuyển, xử l&yacute; kết cấu, giảm chi ph&iacute; thi c&ocirc;ng v&agrave; cả nguy&ecirc;n vật liệu.</span></li>\r\n	<li><span style=\"font-size:18px\">T&ugrave;y biến cao do kết cấu khung thep cho ph&eacute;p chủ đầu tư t&ugrave;y biến theo nhu cầu.</span></li>\r\n	<li><span style=\"font-size:18px\">Lắp dựng nhanh ch&oacute;ng</span></li>\r\n	<li><span style=\"font-size:18px\">Chi ph&iacute; thấp</span></li>\r\n	<li><span style=\"font-size:18px\">Đ&aacute;p ứng mọi tải trọng mong muốn.</span></li>\r\n</ul>\r\n\r\n<h4><span style=\"font-size:18px\"><strong>Nhược điểm của nh&agrave; tiền chế</strong></span></h4>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; tiền chế cũng c&oacute; những nhược điểm nhất định, c&oacute; thể kể đến như:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Độ bền mang t&iacute;nh tương đối.</span></li>\r\n	<li><span style=\"font-size:18px\">L&agrave; kết cấu khung th&eacute;p, n&ecirc;n dễ bị ăn m&ograve;n, han rỉ</span></li>\r\n	<li><span style=\"font-size:18px\">Khả năng chịu lửa, chịu nhiệt k&eacute;m,...</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\">Những nhược điểm n&agrave;y đều c&oacute; c&aacute;ch v&agrave; giải ph&aacute;p để hạn chế sự t&aacute;c động. Với c&aacute;c đơn vị thi c&ocirc;ng nhiều kinh nghiệm v&agrave; uy t&iacute;n như Trường Giang th&igrave; c&ocirc;ng tr&igrave;nh sẽ được đảm bảo v&agrave; cam kết c&aacute;c ti&ecirc;u chuẩn&nbsp;cao hơn.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Cấu tạo của nh&agrave; th&eacute;p tiền chế</strong></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; th&eacute;p tiền chế c&oacute; cấu tạo cơ bản gồm 3 phần:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:18px\"><strong>Khung th&eacute;p chịu lực:</strong>&nbsp;Kết cấu khung th&eacute;p chịu lực bao gồm to&agrave;n bộ phần cấu kiện chịu lực từ phần m&oacute;ng, c&aacute;c trụ cột, dầm s&agrave;n, khung m&aacute;i,...</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Kết cấu phụ:</strong>&nbsp;Khung đỡ, cầu thang, x&agrave; gồ,...</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Vật liệu tạo h&igrave;nh:&nbsp;</strong>Từ v&aacute;ch ngăn, tấm s&agrave;n, m&aacute;i tồn,...</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\">Tr&ecirc;n thực tế sẽ c&ograve;n bao gồm rất nhiều c&aacute;c chi tiết nhỏ cấu tạo v&agrave; tạo n&ecirc;n một nh&agrave; th&eacute;p tiền chế ho&agrave;n chỉnh.&nbsp;</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>X&acirc;y dựng nh&agrave; th&eacute;p tiền chế, quy tr&igrave;nh v&agrave; chi ph&iacute;</strong></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Kh&aacute;c với c&ocirc;ng tr&igrave;nh x&acirc;y dựng truyền thống, nh&agrave; tiền chế khi x&acirc;y dựng sẽ cần t&iacute;nh to&aacute;n v&agrave; thiết kế b&agrave;i bản. C&aacute;c kh&acirc;u đoạn sẽ cần sắp xếp theo một tr&igrave;nh tự hợp l&yacute; để tận dụng tối đa thời gian thi c&ocirc;ng v&agrave; cả chi ph&iacute;. Quy tr&igrave;nh x&acirc;y dựng bao gồm c&aacute;c kh&acirc;u đoạn:</span></p>\r\n\r\n<h4><span style=\"font-size:18px\"><strong>Chuẩn bị x&acirc;y dựng nh&agrave; th&eacute;p tiền chế</strong></span></h4>\r\n\r\n<p><span style=\"font-size:18px\">Rất nhiều chi tiết cần phải chuẩn bị, tuy nhi&ecirc;n c&aacute;c cấu kiện ch&iacute;nh quan trọng bao gồm:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><strong>Khung th&eacute;p v&agrave; c&aacute;c cấu kiện:&nbsp;</strong>Phần n&agrave;y th&ocirc;ng thường được sản xuất theo quy c&aacute;ch thiết kế tại c&aacute;c nh&agrave; m&aacute;y sản xuất khung th&eacute;p chuy&ecirc;n dụng. Khi lắp dựng chỉ cần di chuyển đến c&ocirc;ng tr&igrave;nh theo tiến độ.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>T&ocirc;n m&aacute;i:</strong>&nbsp;Lựa chọn v&agrave; đặt sản xuất t&ocirc;n lợp m&aacute;i theo k&iacute;ch thước ph&ugrave; hợp với thiết kế. T&ocirc;n lợp m&aacute;i c&oacute; thể sử dụng t&ocirc;n lạnh, t&ocirc;n mạ kẽm,...</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Tấm bao che:</strong>&nbsp;Phần n&agrave;y kh&aacute; dễ d&agrave;ng, bạn c&oacute; thể đặt mua hoặc đặt sản xuất theo y&ecirc;u cầu.</span></li>\r\n</ul>\r\n\r\n<h4><span style=\"font-size:18px\"><strong>Giai đoạn x&acirc;y dựng</strong></span></h4>\r\n\r\n<p><span style=\"font-size:18px\">Thực hiện từ&nbsp;c&aacute;c bản vẽ thiết kế&nbsp;kiến tr&uacute;c cho đến c&aacute;c bản vẽ sản xuất thi c&ocirc;ng.&nbsp;Sau đ&oacute;, ho&agrave;n thiện phương &aacute;n thiết kế kiến tr&uacute;c, thiết kế kết cấu, chọn vật liệu v&agrave; thể hiện &yacute; tưởng&nbsp;tr&ecirc;n c&aacute;c bản vẽ phối cảnh, mặt đứng, mặt bằng, mặt cắt...</span></p>\r\n\r\n<h5><span style=\"font-size:18px\"><strong>1. Gia c&ocirc;ng sản xuất khung th&eacute;p</strong></span></h5>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Cắt c&aacute;c phần của khung th&eacute;p: Sử dụng m&aacute;y cắt chuy&ecirc;n dụng (CNC), để cắt từng phần của khung th&eacute;p theo bản&nbsp;thiết kế chuẩn.</span></li>\r\n	<li><span style=\"font-size:18px\">Gia c&ocirc;ng bản m&atilde;: Khoan v&agrave; tạo&nbsp;lỗ cho bản m&atilde;, sau đ&oacute; d&ugrave;ng bu l&ocirc;ng gắn kết c&aacute;c kết cấu th&eacute;p lại.</span></li>\r\n	<li><span style=\"font-size:18px\">Lắp r&aacute;p: C&aacute;c th&agrave;nh phần được nắn chỉnh, uốn v&agrave; tạo h&igrave;nh. Sau đ&oacute; r&aacute;p với nhau như thiết kế v&agrave; định vị bằng gi&aacute; đỡ v&agrave; c&aacute;c mối hạn tạm thời.</span></li>\r\n	<li><span style=\"font-size:18px\">H&agrave;n: Sử dụng c&aacute;c loại m&aacute;y h&agrave;n chuy&ecirc;n dụng (H&agrave;n dưới lớp thuốc, h&agrave;n Mig, han Mag,...)&nbsp;để h&agrave;n c&aacute;c cấu kiện với nhau.</span></li>\r\n	<li><span style=\"font-size:18px\">Nắn chỉnh: Qu&aacute; tr&igrave;nh h&agrave;n v&agrave; lắp r&aacute;p c&oacute; thể khung th&eacute;p sẽ bị biến dạng sai định dạng v&agrave; k&iacute;ch thước. Việc nắn chỉnh để đưa về đ&uacute;ng quy chuẩn&nbsp;</span></li>\r\n	<li><span style=\"font-size:18px\">R&aacute;p bản m&atilde;</span></li>\r\n	<li><span style=\"font-size:18px\">Vệ sinh,...</span></li>\r\n</ul>', 'sdfsfsf', 'ton1548.jpg', '1', 1, '1', 1, '2022-03-08 00:29:46', '2022-03-08 00:29:46'),
(15, 'Công trình nhà thép tiền chế', 'cong-trinh-nha-thep-tien-che', '<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Nh&agrave; th&eacute;p tiền chế (viết tắt l&agrave; p.e.b.) sử dụng c&aacute;c nguy&ecirc;n vật liệu được sản xuất v&agrave; gia c&ocirc;ng sẵn tại nh&agrave; m&aacute;y với k&iacute;ch thước v&agrave; số lượng x&aacute;c định, đ&aacute;p ứng nhiều y&ecirc;u cầu kh&aacute;c nhau về kết cấu v&agrave; t&iacute;nh thẩm mỹ cho từng c&ocirc;ng tr&igrave;nh ri&ecirc;ng biệt. Nhờ sự linh hoạt trong thiết kế, sản xuất &ndash; gia c&ocirc;ng v&agrave; nguồn sản phẩm dồi d&agrave;o, PEB Steel c&oacute; thể ho&agrave;n th&agrave;nh c&aacute;c c&ocirc;ng tr&igrave;nh phức tạp cho nhiều nh&oacute;m ng&agrave;nh v&agrave; mục đ&iacute;ch sử dụng.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Đặc t&iacute;nh sản phẩm</span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Tối ưu chi ph&iacute; đầu tư</span></span></li>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Nguy&ecirc;n vật liệu cao cấp</span></span></li>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Thi c&ocirc;ng nhanh ch&oacute;ng</span></span></li>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Chức năng linh hoạt &amp; tối ưu kiến tr&uacute;c</span></span></li>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Tối ưu chi ph&iacute; vận h&agrave;nh v&agrave; bảo tr&igrave;.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\"><img alt=\"Nhà thép tiền chế của PEB Steel\" src=\"https://pebsteel.com/wp-content/uploads/2020/07/Pre-engineered-buildings-En-Full-elements_ReLayout-01-3.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Nh&agrave; th&eacute;p tiền chế của PEB Steel gồm ba th&agrave;nh phần ch&iacute;nh:<br />\r\n&ndash;&nbsp; &nbsp; &nbsp;Tổ hợp cột v&agrave;&nbsp;<strong><a href=\"https://pebsteel.com/vi/kien-thuc-peb/tim-hieu-khung-keo-thep-cua-peb-steel/\">k&egrave;o th&eacute;p</a></strong>&nbsp;h&igrave;nh chữ &ldquo;<strong>I</strong>&rdquo;<br />\r\n&ndash;&nbsp; &nbsp; &nbsp;X&agrave; gồ mạ kẽm h&igrave;nh chữ &ldquo;<strong>Z</strong>&rdquo; v&agrave; &ldquo;<strong>C</strong>&rdquo; gồm x&agrave; gồ m&aacute;i, x&agrave; gồ v&aacute;ch, x&agrave; gồ&nbsp;<strong><a href=\"https://pebsteel.com/vi/san-pham/mang-xoi-ong-xoi/\">m&aacute;ng xối</a></strong><br />\r\n&ndash;&nbsp; &nbsp; &nbsp;T&ocirc;n m&aacute;i, t&ocirc;n v&aacute;ch</span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\"><img alt=\"Kết cấu nhà thép tiền chế của PEB Steel\" src=\"https://pebsteel.com/wp-content/uploads/2020/07/Main-frame.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Ngo&agrave;i ra, nh&agrave; th&eacute;p tiền chế c&ograve;n c&oacute; c&aacute;c kết cấu phụ v&agrave; phụ kiện để đảm bảo chức năng sử dụng v&agrave; t&iacute;nh thẩm mỹ t&ugrave;y theo y&ecirc;u cầu của chủ đầu tư:<br />\r\n&ndash;&nbsp; &nbsp;&nbsp;<strong>&nbsp;Kết cấu phụ:</strong>&nbsp;kệ s&agrave;n mezzanine hay s&agrave;n tầng lửng (bao gồm dầm v&agrave; s&agrave;n), dầm cầu trục (n&acirc;ng đỡ hệ thống&nbsp;<strong><a href=\"https://pebsteel.com/vi/san-pham/cau-truc/\">cầu trục</a></strong>), hệ trợ lực m&aacute;i, lối đi,&hellip;</span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\"><img alt=\"Kết cấu phụ nhà thép tiền chế của PEB Steel\" src=\"https://pebsteel.com/wp-content/uploads/2020/07/Purlins-and-girts.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">&ndash;&nbsp; &nbsp; &nbsp;<strong>Phụ kiện v&agrave; chi tiết thẩm mỹ</strong>:&nbsp;<a href=\"https://pebsteel.com/vi/san-pham/mat-dung/\"><strong>mặt dựng</strong></a>&nbsp;trang tr&iacute;, m&aacute;i che, m&aacute;i mở rộng,&nbsp;<a href=\"https://pebsteel.com/vi/san-pham/diem-mai-diem-goc/\"><strong>diềm</strong></a>&nbsp;ốp trang tr&iacute;, m&aacute;ng xối, ống xối, c&aacute;p giằng mạ kẽm, th&ocirc;ng gi&oacute; đỉnh m&aacute;i,&hellip;</span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\"><img alt=\"Phụ kiện và chi tiết thẩm mỹ nhà thép tiền chế của PEB Steel\" src=\"https://pebsteel.com/wp-content/uploads/2020/07/functional-1.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Nh&agrave; th&eacute;p tiền chế hiện nay rất được ưa chuộng nhờ gi&uacute;p tiết kiệm chi ph&iacute; x&acirc;y dựng cho chủ đầu tư cũng như r&uacute;t ngắn đ&aacute;ng kể thời gian ho&agrave;n th&agrave;nh so với c&aacute;c h&igrave;nh thức x&acirc;y dựng truyền thống.</span></span></p>\r\n\r\n<p><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\"><img alt=\"nhà thép tiền chế - 4\" src=\"https://pebsteel.com/wp-content/uploads/2020/07/Aesthetic.jpg\" /></span></span></p>\r\n\r\n<h2><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:18px\">Th&ecirc;m th&ocirc;ng tin<br />\r\nvề sản phẩm n&agrave;y</span></span></h2>', 'fsdsfsdfsdfsfsdfsdfsdfsdf', '190.jpg', '1', 1, '1', 1, '2022-03-09 02:05:53', '2022-03-09 02:05:53');
INSERT INTO `posts` (`id`, `title`, `slug_title`, `content`, `summary`, `images`, `hot`, `trending`, `active`, `type_id`, `created_at`, `updated_at`) VALUES
(16, 'QUY TRÌNH THI CÔNG NHÀ XƯỞNG CÔNG NGHIỆP TỪ A ĐẾN Z', 'quy-trinh-thi-cong-nha-xuong-cong-nghiep-tu-a-den-z', '<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Quy tr&igrave;nh<strong>&nbsp;thi c&ocirc;ng nh&agrave; xưởng</strong>&nbsp;c&oacute; ảnh hưởng quan trọng đến tiến độ v&agrave; chất lượng nh&agrave; xưởng sau khi đưa v&agrave;o sản xuất. L&agrave; đơn vị đi đầu trong lĩnh vực x&acirc;y dựng c&ocirc;ng nghiệp, Wedo l&agrave; đơn vị ti&ecirc;n phong, x&acirc;y dựng cho m&igrave;nh một qu&aacute; tr&igrave;nh thi c&ocirc;ng chi tiết v&agrave; chuy&ecirc;n nghiệp.</span></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&aacute;c loại nh&agrave; xưởng Wedo thi c&ocirc;ng</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hiện nay tr&ecirc;n thị trường, nh&agrave; xưởng được ph&acirc;n loại kh&aacute; đa dạng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ph&acirc;n theo c&ocirc;ng năng c&oacute;:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng bao gồm c&aacute;c khối xưởng 1, 2, 3 để đặt m&aacute;y m&oacute;c, nguy&ecirc;n liệu, th&agrave;nh phẩm</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng bao gồm khối văn&nbsp; ph&ograve;ng ở ph&iacute;a trước hoặc b&ecirc;n cạnh nh&agrave; xưởng để vừa l&agrave;m việc, vừa sản xuất</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng ph&acirc;n theo độ cao gồm:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng cao từ 8m &ndash; 12m bao gồm n&oacute;c gi&oacute;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng cao từ 6m &ndash; 8m bao gồm n&oacute;c gi&oacute;</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tuy nhi&ecirc;n c&aacute;ch ph&acirc;n loại theo vật tư thi c&ocirc;ng&nbsp; hiện nay được sử dụng nhiều nhất v&agrave; cũng được thi c&ocirc;ng nhiều nhất. Nh&agrave; xưởng ph&acirc;n theo vật liệu, vẫn c&oacute; thể thiết kế v&agrave; thi c&ocirc;ng bao gồm nh&agrave; xưởng theo khẩu độ, nh&agrave; xưởng theo chức năng.</span></span></p>\r\n\r\n<p><input alt=\"\" src=\"D:\\nhatheptienche\\8.jpg\" type=\"image\" /><input alt=\"\" src=\"D:\\nhatheptienche\\8.jpg\" type=\"image\" /></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thiết kế nh&agrave; xưởng c&ocirc;ng nghiệp hiện nay đang ph&aacute;t triển mạnh</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng ph&acirc;n theo vật tư thi c&ocirc;ng bao gồm:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><a href=\"https://wedo.vn/nha-xuong-khung-thep/\">Nh&agrave; xưởng khung th&eacute;p</a>&nbsp;tiền chế</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng to&agrave;n bộ nh&agrave; xưởng từ cột, dầm đều bằng k&egrave;o th&eacute;p trừ phần m&oacute;ng . Phần m&oacute;ng bằng b&ecirc; t&ocirc;ng cốt th&eacute;p rồi đặt bulong neo định vị để chờ dựng cột th&eacute;p nh&agrave; xưởng.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tường nh&agrave; xưởng bằng gạch x&acirc;y d&agrave;y 10cm, hoặc 20cm cao khoảng 2,2m đến 2,8m sau đ&oacute; l&agrave;m v&aacute;ch tole t&ugrave;y theo hồ sơ thiết kế.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">M&aacute;i nh&agrave; xưởng sử dụng tole m&agrave;u mạ kẽm + d&aacute;n tấm c&aacute;ch nhiệt nhằm chống n&oacute;ng, chống ồn. Sử dụng x&agrave; gồ đen hoặc mạ kẽm d&agrave;y 1.4l-2.0ly&hellip;</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng b&ecirc; t&ocirc;ng cốt th&eacute;p</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">+ Thi c&ocirc;ng to&agrave;n bộ nh&agrave; xưởng từ m&oacute;ng, cột, dầm đều bằng b&ecirc; t&ocirc;ng cốt th&eacute;p.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">+ Tường<strong>&nbsp;</strong>nh&agrave; xưởng x&acirc;y bằng gạch d&agrave;y 10cm, hoặc 20cm t&ugrave;y theo hồ sơ thiết kế.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">+ M&aacute;i nh&agrave; xưởng sử dụng tole m&agrave;u mạ kẽm + d&aacute;n tấm c&aacute;ch nhiệt nhằm chống n&oacute;ng, chống ồn. Sử dụng x&agrave; gồ đen hoặc mạ kẽm d&agrave;y 1.4l-2.0ly..</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng b&ecirc; t&ocirc;ng cốt th&eacute;p v&agrave; nh&agrave; xưởng khung th&eacute;p tiền chế l&agrave; hạng mục ch&uacute;ng t&ocirc;i triển khai thi c&ocirc;ng mạnh nhất ở c&aacute;c tỉnh th&agrave;nh, khu c&ocirc;ng nghiệp.</span></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&aacute;c yếu tố cần phải l&agrave;m r&otilde; trước khi thi c&ocirc;ng nh&agrave; xưởng</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nếu&nbsp;<a href=\"https://wedo.vn/nha-xuong-la-gi-quy-chuan-va-tieu-chuan-thiet-ke-nha-xuong-hien-nay/\">nh&agrave; xưởng</a>&nbsp;nằm tr&ecirc;n v&ugrave;ng đất cứng, c&oacute; cao độ cao so với cost nền&nbsp;x&acirc;y th&igrave; khi thi c&ocirc;ng phần m&oacute;ng sẽ kh&ocirc;ng cần gia cố m&oacute;ng như &eacute;p cọc, đ&oacute;ng cừ tr&agrave;m. Ngược lại, nếu thi c&ocirc;ng m&oacute;ng&nbsp; nh&agrave; xưởng nằm tr&ecirc;n v&ugrave;ng đất yếu, đất b&ugrave;n th&igrave; phần gia cố m&oacute;ng rất quan trọng đối với việc x&acirc;y dựng xưởng.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khi thi c&ocirc;ng nh&agrave; xưởng cần ch&uacute; trọng đến m&oacute;ng, s&agrave;n, kết cấu th&eacute;p</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phần nền&nbsp;nh&agrave; xưởng&nbsp;th&igrave; t&ugrave;y theo c&ocirc;ng năng sử dụng của nh&agrave; xưởng m&agrave; đơn vị&nbsp; thiết kế thi c&ocirc;ng nh&agrave; xưởng c&oacute; c&aacute;ch bố tr&iacute; th&eacute;p s&agrave;n nh&agrave; xưởng sao cho hợp l&yacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phần đổ b&ecirc; t&ocirc;ng nh&agrave; xưởng theo độ d&agrave;y 10,20,30 hay 50cm cần phải x&aacute;c định r&otilde; theo hồ sơ thiết kế thi c&ocirc;ng, bởi cỏ những nh&agrave; xưởng đặt m&aacute;y m&oacute;c, thiết bị sản xuất c&oacute; tải trong l&ecirc;n đến v&agrave;i chục tấn/m2.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phần cột th&eacute;p<strong>,&nbsp;</strong>k&egrave;o th&eacute;p nh&agrave; xưởng phải thiết kế vừa phải tr&aacute;nh bố tr&iacute; th&eacute;p thiếu hoặc dư. Theo ti&ecirc;u chuẩn thiết kế&nbsp;nh&agrave; xưởng&nbsp;th&ocirc;ng thường th&igrave; 1m khoảng 20-32kg th&eacute;p t&ugrave;y theo qui m&ocirc; nh&agrave; xưởng, mức độ đầu tư t&agrave;i ch&iacute;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">S&agrave;n b&ecirc; t&ocirc;ng sau khi đổ xong cần được đ&aacute;nh b&oacute;ng, để đảm bảo kết cấu, đồng thời chống b&aacute;m bụi v&agrave; dễ vệ sinh</span></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Quy tr&igrave;nh thi c&ocirc;ng nh&agrave; xưởng tại WEDO</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Quy tr&igrave;nh&nbsp;<a href=\"https://wedo.vn/xay-nha-xuong/\">x&acirc;y nh&agrave; xưởng</a>&nbsp;chuy&ecirc;n nghiệp của WEDO sẽ trải qua 3 giai đoạn:</span></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Giai đoạn 1: Tiếp nhận hồ sơ thi c&ocirc;ng nh&agrave; xưởng</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 1: Tiếp nhận th&ocirc;ng tin về dự &aacute;n</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Th&ocirc;ng tin về dự &aacute;n được cung cấp từ chủ đầu tư. Ngo&agrave;i ra chủ thầu cũng chủ động trong việc t&igrave;m hiểu th&ocirc;ng tin qua nhiều k&ecirc;nh.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Những th&ocirc;ng tin quan trọng về mộ dự &aacute;n m&agrave; mọi người cần phải nắm gồm c&oacute;: quy m&ocirc;, tiến độ thi c&ocirc;ng, phải biết được người m&igrave;nh cần li&ecirc;n hệ trực tiếp về dự &aacute;n.</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tiếp nhận hồ sơ thi c&ocirc;ng l&agrave; bước quan trọng để tiến h&agrave;nh lập dự to&aacute;n</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 2: Trao đổi dự &aacute;n với chủ đầu tư x&acirc;y nh&agrave; xưởng</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chủ động li&ecirc;n hệ với người quản l&yacute; dự &aacute;n, mang theo t&agrave;i liệu, catolo của c&ocirc;ng ty</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hẹn gặp trực tiếp, n&oacute;i chuyện l&agrave;m quen với kh&aacute;ch h&agrave;ng, cần lấy tất cả c&aacute;c th&ocirc;ng tin về dự &aacute;n như vốn đầu tư của kh&aacute;ch h&agrave;ng, kh&aacute;ch h&agrave;ng l&agrave; đơn vị n&agrave;o, c&oacute; tiềm năng ph&aacute;t triển hay kh&ocirc;ng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave;y tỏ cho họ m&igrave;nh muốn đi theo dự &aacute;n n&agrave;y v&agrave; m&igrave;nh sẽ l&agrave;m thật tốt, h&agrave;i l&ograve;ng với chủ đầu tư.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 3 : B&aacute;o gi&aacute; thi c&ocirc;ng</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kiểm tra hồ sơ thiết kế nh&agrave; xưởng do chủ đầu tư b&agrave;n giao, tiến h&agrave;nh b&oacute;c t&aacute;ch hồ sơ kỹ thuật thi c&ocirc;ng</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bao gồm vật tư sử dụng cho từng hạng mục cụ thể</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phương &aacute;n thi c&ocirc;ng theo hồ sơ</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&iacute;nh khối lượng thi c&ocirc;ng &nbsp;v&agrave; b&aacute;o gi&aacute;</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 4 : K&yacute; hợp đồng thi c&ocirc;ng</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khi b&ecirc;n chủ đầu tư chấp thuận b&aacute;o gi&aacute; thi c&ocirc;ng, tiến h&agrave;nh k&yacute; hợp đồng, hai b&ecirc;n sẽ b&agrave;n bạc về c&aacute;c điều khoản v&agrave; tiến độ thi c&ocirc;ng theo hợp đồng</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hợp đồng&nbsp;<strong><a href=\"https://wedo.vn/quy-trinh-thi-cong-nha-xuong-cong-nghiep-tu-a-den-z/\">thi c&ocirc;ng nh&agrave; xưởng</a></strong>&nbsp;phải ghi r&otilde; điều khoản thanh to&aacute;n cũng như thời gian sản xuất v&agrave; lắp dựng dự &aacute;n</span></span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Giai đoạn 2 : Tiến h&agrave;nh gia c&ocirc;ng cấu kiện th&eacute;p</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 1 : Chuẩn bị cho c&ocirc;ng t&aacute;c gia c&ocirc;ng</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kỹ sư phải xem hồ sơ, triển khai bản vẽ shop Drawing, cho ra chi tiết c&aacute;c c&acirc;y th&eacute;p, từng cấu kiện.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&ecirc;n kế hoạch v&agrave; đặt h&agrave;ng th&eacute;p h&igrave;nh, th&eacute;p tấm chờ về nh&agrave; m&aacute;y chuẩn bị cho c&ocirc;ng t&aacute;c gia c&ocirc;ng</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 2 : Gia c&ocirc;ng cấu kiện tại nh&agrave; m&aacute;y</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau khi xem x&eacute;t hồ sơ, k&yacute; hợp đồng thi c&ocirc;ng, sẽ tiến h&agrave;nh gia c&ocirc;ng cấu kiện th&eacute;p tại nh&agrave; m&aacute;y</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tiến h&agrave;nh kiểm tra v&agrave; đ&aacute;nh gi&aacute; chủng loại th&eacute;p, quy c&aacute;ch xuất xứ vật tư đ&uacute;ng như hồ sơ kỹ thuật. Sau khi tất cả được kiểm tra v&agrave; kh&ocirc;ng c&oacute; vấn đề g&igrave; ta tiến h&agrave;nh c&ocirc;ng t&aacute;c gia c&ocirc;ng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Quy tr&igrave;nh gia c&ocirc;ng cấu kiện th&eacute;p để phục vụ v&agrave;o x&acirc;y nh&agrave; xưởng gồm</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cắt th&eacute;p. khi cắt th&eacute;p phải mời tư vấn gi&aacute;m s&aacute;t chủ đầu tư đến chứng kiến lấy mẫu th&iacute; nghiệm.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">R&aacute;p tổ hợp, r&aacute;p c&aacute;c miếng th&eacute;p th&agrave;nh h&igrave;nh cấu kiện.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">H&agrave;n tổ hợp cấu kiện.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vệ sinh c&aacute;c cấu kiện, chuẩn bị phun bi.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tiến h&agrave;nh sơn chống rỉ.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chờ sơn chống rỉ kh&ocirc;, ta sơn ho&agrave;n thiện, chiều d&agrave;y sơn phải đảm bảo đ&uacute;ng với thiết kế.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khi sơn ho&agrave;n thiện đ&atilde; kh&ocirc;, để bảo quản vật tư kh&ocirc;ng bị dơ bới bụi v&agrave; thời tiết, ta tiến h&agrave;nh bọc một lớp nilon chống bụi đồng thời đ&aacute;nh số cấu kiện tr&ecirc;n vị tr&iacute; lắp đặt.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">H&agrave;ng đ&atilde; sản xuất xong th&igrave; tập kết ra c&ocirc;ng trường th&ocirc;i, lưu &yacute; b&aacute;o cho b&ecirc;n lắp dựng c&ocirc;ng trường biết xe c&oacute; cẩu hay kh&ocirc;ng c&oacute; cẩu.</span></span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Giai đoạn 3 : Thi c&ocirc;ng lắp dựng kết cấu th&eacute;p tại c&ocirc;ng trường</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>&nbsp;Bước&nbsp;</strong><strong>1</strong><strong>:&nbsp;</strong><strong>Thi c&ocirc;ng nền m&oacute;ng</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng nền m&oacute;ng nh&agrave; xưởng</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">San lắp đất nền : Đ&acirc;y l&agrave; việc đầu ti&ecirc;n nh&agrave; thầu cần l&agrave;m, t&ugrave;y theo độ của nền đất hiện hữu m&agrave; nh&agrave; thầu triển khai san lắp nền ph&ugrave; hợp với bản vẽ kỹ thuật thi c&ocirc;ng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Định vị trục tim : C&ocirc;ng t&aacute;c n&agrave;y rất quan trọng. Sau n&agrave;y c&aacute;c vị tr&iacute; m&oacute;ng cột sẽ theo tim trục n&agrave;y m&agrave; thi c&ocirc;ng dựa tr&ecirc;n bản vẽ.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đ&agrave;o m&oacute;ng h&agrave;ng r&agrave;o : H&agrave;ng r&agrave;o nh&agrave; xưởng thường rất cao v&agrave; d&agrave;i, do đ&oacute; phần m&oacute;ng h&agrave;ng r&agrave;o được thi c&ocirc;ng ki&ecirc;n cố</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng m&oacute;ng v&agrave; đ&agrave; kiềng : Sau khi c&oacute; tim trục th&igrave; sẽ thi c&ocirc;ng m&oacute;ng. Th&ocirc;ng thường m&oacute;ng nh&agrave; xưởng được thiết kế l&agrave; m&oacute;ng đơn hay m&oacute;ng cọc, vật liệu l&agrave; b&ecirc; t&ocirc;ng cốt th&eacute;p,. C&aacute;c bu l&ocirc;ng cột được ch&ocirc;n trong m&oacute;ng chờ sẵn, sau n&agrave;y lắp gh&eacute;p với cột th&eacute;p.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lu l&egrave;n nền đất : Nền đất được san lấp sau đ&oacute; lu l&egrave;n cho đ&uacute;ng độ chặt m&agrave; thiết kế y&ecirc;u cầu</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lu nền đ&aacute; cho xưởng : Nền nh&agrave; xưởng thường l&agrave; b&ecirc; t&ocirc;ng cốt th&eacute;p đặt tr&ecirc;n nền đ&aacute; đ&atilde; được lu l&egrave;n đủ độ chặt. Chiều d&agrave;y lớp đ&aacute; do thiết kế quy định.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng nền xưởng : Thực hiện c&ocirc;ng t&aacute;c cốt th&eacute;p, đổ b&ecirc; t&ocirc;ng. C&ocirc;ng t&aacute;c bảo dưỡng b&ecirc; t&ocirc;ng sau khi đổ rất quan trọng , phải thực hiện đ&uacute;ng quy định để tr&aacute;nh nứt b&ecirc; t&ocirc;ng s&agrave;n.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước&nbsp;</strong><strong>2</strong><strong>:</strong><strong>&nbsp;Thi c&ocirc;ng khung th&eacute;p&nbsp;</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng lắp gh&eacute;p khung th&eacute;p bằng cần cẩu</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&aacute;c bộ phận kết cấu th&eacute;p được gia c&ocirc;ng tại nh&agrave; m&aacute;y với chất lượng được kiểm so&aacute;t chặt chẽ sau đ&oacute; vận chuyển ra c&ocirc;ng trường. Tại c&ocirc;ng trường ch&uacute;ng được lắp gh&eacute;p với nhau tạo th&agrave;nh khung th&eacute;p. Đ&acirc;y l&agrave; bộ phận xương sống của nh&agrave; xưởng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lắp dựng khung th&eacute;p : Thường d&ugrave;ng cần cẩu n&acirc;ng v&agrave; đặt c&aacute;c bộ phận kết cấu th&eacute;p v&agrave;o vị tr&iacute;. C&aacute;c bộ phận kết nối với nhau bằng bu l&ocirc;ng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lắp dựng x&agrave; gồ v&agrave; c&aacute;p giằng : Lắp hệ giằng đảm bảo ổn định ngo&agrave;i mặt phẳng khung. Hệ x&agrave; gồ cũng c&oacute; t&aacute;c dụng tăng cường độ ổn định của khung th&eacute;p v&agrave; d&ugrave;ng để n&acirc;ng đỡ tấm lợp.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 3. Thi c&ocirc;ng vỏ bao ch</strong>e</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vỏ bao che nh&agrave; xưởng bao gồm phần tường v&agrave; m&aacute;i. Tiến h&agrave;nh x&acirc;y tường bao che, thi c&ocirc;ng m&aacute;i t&ocirc;n cho nh&agrave; xưởng theo hồ sơ kỹ thuật trước đ&oacute;.</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng phần bao che cho nh&agrave; xưởng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước&nbsp;</strong><strong>4</strong><strong>:</strong><strong>&nbsp;Thi c&ocirc;ng hạ tầng</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hạ tầng nh&agrave; xưởng bao gồm : đường giao th&ocirc;ng, ống cấp tho&aacute;t nước.. Nền đường cần được lu l&egrave;n đạt y&ecirc;u cầu để chịu được c&aacute;c loại xe tải trọng lớn lưu th&ocirc;ng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lắp ống tho&aacute;t nước</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lu nền đường</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lu đ&aacute; nền đường</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bảo dưỡng b&ecirc; t&ocirc;ng nền đường, cắt ron chống nứt</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng hạ tầng cho nh&agrave; xưởng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 5: Thi c&ocirc;ng hệ thống kỹ thuật</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hệ thống kỹ thuật bao gồm hệ thống ph&ograve;ng ch&aacute;y chữa ch&aacute;y, hệ thống th&ocirc;ng tin li&ecirc;n lạc, hệ thống điện, hệ thống kỹ thuật phục vụ sản xuất..</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi c&ocirc;ng bể chứa nước ngầm phục vụ PCCC</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lắp đặt hệ thống chữa ch&aacute;y</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đi &acirc;m đường truyền hệ thống th&ocirc;ng tin li&ecirc;n lạc</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 6: Ho&agrave;n thiện</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kẻ vạch ph&acirc;n l&agrave;n giao th&ocirc;ng trong xưởng</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đ&oacute;ng trần thạch cao nh&agrave; văn ph&ograve;ng</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trồng cỏ, tạo mảng xanh xung quanh nh&agrave; xưởng, n&acirc;ng cao t&iacute;nh thẩm mỹ của nh&agrave; xưởng</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau ho&agrave;n thiện sẽ tiến h&agrave;nh nghiệm thu v&agrave; b&agrave;n giao nh&agrave; xưởng cho chủ đầu tư</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Bước 7: Nghiệm thu v&agrave; b&agrave;n giao</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; xưởng được l&agrave;m vệ sinh tổng thể trước khi b&agrave;n giao cho chủ đầu tư. Tiến h&agrave;nh nghiệm thu v&agrave; b&agrave;n giao c&ocirc;ng tr&igrave;nh ho&agrave;n thiện.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Với quy tr&igrave;nh chi tiết, chuy&ecirc;n nghiệp, doanh nghiệp c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về dịch vụ&nbsp;<strong>thi c&ocirc;ng nh&agrave; xưởng</strong>&nbsp;của ch&uacute;ng t&ocirc;i. H&atilde;y li&ecirc;n hệ ngay để được tư vấn chi tiết theo dự &aacute;n của từng doanh nghiệp.</span></span></p>', 'Phần đổ bê tông nhà xưởng theo độ dày 10,20,30 hay 50cm cần phải xác định rõ theo hồ sơ thiết kế thi công, bởi cỏ những nhà xưởng đặt máy móc, thiết bị sản xuất có tải trong lên đến vài chục tấn/m2.Phần cột thép, kèo thép nhà xưởng phải thiết kế vừa phải tránh bố trí thép thiếu hoặc dư. Theo tiêu chuẩn thiết kế nhà xưởng thông thường thì 1m khoảng 20-32kg thép tùy theo qui mô nhà xưởng, mức độ đầu tư tài chính.', '379.jpg', '1', 1, '1', 1, '2022-03-13 19:25:43', '2022-03-13 19:25:43'),
(17, 'ĐƠN GIÁ XÂY DỰNG NHÀ XƯỞNG KHUNG THÉP 2020', 'don-gia-xay-dung-nha-xuong-khung-thep-2020', '<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một trong những vấn đề được nhiều chủ đầu tư là doanh nghiệp hiện nay đang dành mối quan tâm lớn chính là&nbsp;<strong>đơn giá xây dựng nhà xưởng khung thép</strong>. Đơn giá hiện nay được tính như thế nào? Những yếu tố nào tác động đến đơn giá xây dựng nhà xưởng khung thép?</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Những thắc mắc này sẽ được chúng tôi làm rõ trong phạm vi bài viết nhỏ này, hy vọng những thông tin chúng tôi sắp chia sẻ, sẽ giúp các bạn có kế hoạch chủ động hơn trong công cuộc đầu tư, xây dựng nhà xưởng kinh doanh.</span></span>​​​​​​</p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tìm hiểu về mô hình nhà xưởng khung thép hiện nay</span></span></h2>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khái niệm</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhà xưởng khung thép hay còn gọi là nhà thép tiền chế, là loại nhà có kết cấu từ các cấu kiện được chế tạo sẵn theo thiết kế tại nhà máy, được kiểm tra chất lượng kỹ trước khi vận chuyển ra công trường lắp dựng.</span></span></p>\r\n\r\n<p>&nbsp;<img src=\"https://i.imgur.com/AdEYvje.jpg\" style=\"font-size: 1rem;\" width=\"512\"></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhà xưởng khung thép hiện đang là xu hướng thiết kế được nhiều doanh nghiệp lựa chọn</span></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cấu tạo</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cấu tạo chính của&nbsp;<a href=\"https://wedo.vn/nha-xuong-khung-thep/\">nhà xưởng khung thép</a>&nbsp;bao gồm các bộ phận chính sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Kết cấu chính:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Được xây dựng với mục đích chịu trọng lực, chịu được sức gió, chịu lực tác động từ bên ngoài.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu chính của nhà xưởng khung thép gồm móng, dầm, kết cấu mang lực mái, dầm cầu chạy, hệ khung chống gió, hệ giằng và cột kèo hình chữ T làm khung chính.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Để tính được&nbsp;<strong>đơn giá xây dựng nhà xưởng khung thép</strong>, cần phải tính toán chi tiết hệ kết cấu chính của xưởng.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hệ kết cấu chính được phân thành:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu chịu lực theo phương đứng: tường, các cửa mái đứng, cửa đi, cửa sổ, …</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu chịu lực theo phương ngang: mái, cửa mái nằm ngang</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu chính nhận tải trọng của toàn bộ nhà xưởng và truyền lực xuống đất bao gồm:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">+ Tải trọng cố định nhằm chịu lực toàn bộ ngôi nhà và các thiết bị được bố trí cố định</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">+ Tải trong không cố định là các yếu tố như con người, các vật vận chuyển hay áp lực gió</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Kết cấu phụ</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu phụ bao gồm vách ngăn, hệ khung đỡ có vách ngăn, hệ sàn công tác, cầu thang và hệ thống xà gồ gồm có xà gồ mái, xà gồ tường hình chữ C và Z</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu nhà xưởng khung thép có phần chính, phụ và kết cấu bao che tạo hình</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Kết cấu bao che và tạo hình</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu bao che và tạo hình sử dụng mái tôn và tôn tường, nhằm giới hạn không gian nhà xưởng, bảo vệ nhà xưởng. Hệ kết cấu này được phân thành:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu bao che theo phương đứng: tường, cửa đi, cửa sổ và cửa mái đứng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kết cấu bao che theo phương ngang: mái, cửa mái nằm ngang</span></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ưu điểm</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><a href=\"https://wedo.vn/quy-trinh-thiet-ke-thi-cong-nha-xuong-tien-che-tron-goi/\">Nhà xưởng tiền chế</a>&nbsp;có nhiều ưu điểm nổi bật đó là:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Có khả năng chịu lực, khó biến dạng qua thời gian sử dụng</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trọng lượng nhẹ, cấu trúc kiến trúc nhà xưởng đơn giản, dễ xây dựng</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi công nhanh chóng, các chi tiết được thiết kế sẵn tại nhà xưởng cho nên khi tới công trình chỉ tiến hành lắp ráp</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tiết kiệm được nhiều chi phí đầu tư cũng như thời gian thi công</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tính công nghiệp hóa cao, cho phép xâu dụng nhiều công trình trên quy mô lớn có chiều rộng lên đến 60m2</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tính chuyển đổi cao, khi muốn mở rộng xưởng, bởi sự linh hoạt trong việc tháo lắp hệ kết cấu khung chính</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Các yếu tố ảnh hưởng đến đơn giá xây dựng nhà xưởng</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Để có thể xác định được&nbsp;<strong><a href=\"https://wedo.vn/don-gia-xay-dung-nha-xuong-khung-thep-2020/\">đơn giá xây dựng nhà xưởng khung thép</a></strong>, cần phải nắm bắt được các yếu tố tác động và ảnh hưởng đến đơn giá xây dựng nhà xưởng như sau:</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Có nhiều yếu tố ảnh hưởng đến đơn giá xây dựng nhà xưởng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Công năng và mục đích sử dụng:</strong>&nbsp;công năng và mục đích sử dụng của nhà xưởng sẽ ảnh hưởng đến mẫu thiết kế nhà xưởng, kết cấu chịu lực chung. Đơn giá xây dựng mỗi nhà xưởng được sử dụng cho mỗi mục đích khác nhau là không giống nhau.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Quy mô nhà xưởng:</strong>&nbsp;quy mô mỗi nhà xưởng với thông số diện tích khác nhau, từ 500m2, 100m2, 10000m2, 20000m2, … với những công trình nhà xưởng có diện tích càng lớn thì chi phí xây dựng tính theo m2 sẽ nhỏ hơn so với những nhà xưởng có diện tích quy mô xây dựng nhỏ.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Vật liệu xây dựng:</strong>&nbsp;có nhiều vật liệu xây dựng sử dụng cho việc thi công&nbsp;<a href=\"https://wedo.vn/xay-nha-xuong/\">xây nhà xưởng</a>. Với những nhà xưởng đơn giản thì chi phí thi công sẽ thấp hơn những loại hình nhà xưởng khác. Đối với những mẫu nhà xưởng dùng để sản xuất thực phẩm, gia công cơ khí hay các thiết bị điện tử thường có giá trị cao về kinh tế, do đó yêu cầu cao về vật liệu xây dựng cũng như chi phí sẽ cao hơn những mẫu nhà xưởng khác.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Thời gian thi công:</strong>&nbsp;với những mẫu nhà xưởng yêu cầu tiến độ thi công gấp thì đơn giá xây dựng thường có chi phí cao hơn những mẫu nhà được xây dựng theo tiến độ đã hoạch định.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Nhà thầu xây dựng:</strong>&nbsp;mỗi đơn vị nhà thầu xây dựng sẽ có những đơn giá khác nhau. Việc tìm được nhà thầu thi công uy tín, giá cả phải chăng là cả vấn đề. Giá cao thì sẽ đội chi phí, mà giá thấp thì chưa chắc đã đảm bảo chất lượng tuổi thọ công trình.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đơn giá xây dựng nhà xưởng khung thép chịu tác động của nhiều yếu tố.</span></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đơn giá xây dựng nhà xưởng tiền chế hiện nay</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Dựa trên các yếu tố tác động đến chi phí xây dựng nhà xưởng khung thép, với kinh nghiệm nhiều năm trong lĩnh vực thiết kế và thi công nhà xưởng, Wedo xin được công khai bảng giá xây dựng chung của nhà xưởng như sau:</span></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Đơn giá xây dựng cho nhà xưởng khung thép</strong>&nbsp;tiền chế vượt nhịp 20 đến 30m</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đối với các công trình có hệ vượt nhịp từ 20m đến 30m thì có đơn giá xây dựng từ 1.600.000 đến 2.500.000đ/m2</span></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đơn giá xây dựng cho nhà xưởng khung thép có diện tích dưới 1500m2, cao độ dưới 7,5m2</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cột xây bằng lõi thép hoặc cột đổ bê tông, kèo thép chữ V, sắt hộp, vách xây tường 100mm, cách tôn, mái tôn có giá từ 1.800.000 đến 2.000.000đ/m2.</span></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đơn giá xây dựng cho nhà xưởng khung thép hoàn thiện có đổ bê tông</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Có mái tôn, xà gồ, móng đơn, dây cáp căng, xoa nền bê ông, bột trét, cửa cuốn thông thường, cửa sổ, có đơn giá từ 1.100.000 đến 1.900.000đ/m2</span></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đơn giá xây dựng cho nhà xưởng khung thép không đổ bê tông</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đơn giá xây dựng cho nhà xưởng khung thép đơn giản này có giá từ 450.000 đến 1.200.000đ/m2</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mức giá xây dựng nhà xưởng có nhiều định mức</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Đơn giá xây dựng nhà xưởng khung thép</strong>&nbsp;ngoài phụ thuộc những yếu tố chính ở trên, nó còn chịu tác động của thời gian xây dựng cụ thể. Đơn giá mỗi năm mỗi khác. Do đó nếu muốn có đơn giá xây dựng nhà xưởng khung thép khoa học và hợp chi phí, hãy liên hệ với Wedo để chúng tôi tư vấn cụ thể hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Là đơn vị đi đầu trong lĩnh vực thiết kế xây dựng nhà xưởng, Wedo với đội ngũ&nbsp; nhân sự giàu kinh nghiệm, vững về chuyên môn, hứa hẹn sẽ đem đến cho doanh nghiệp của các bạn phương án thiết kế nhà xưởng khoa học nhất.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngoài việc tối ưu hóa chi phí đầu tư, phương án thiết kế nhà xưởng khung thép của Wedo luôn đảm bảo kết cấu bền vững, phân chia phân xưởng khoa học kết hợp với cảnh quan hoàn thiện có tính hài hòa và thẩm mĩ cao.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mọi tư vấn thiết kế nhà xưởng khung thép nói riêng cũng như những vấn đề liên quan đến đầu tư xây dựng, kết cấu, thẩm định dự án nhà máy, xin liên hệ hotline: 024 3618 8888.</span></span></p>', 'Được xây dựng với mục đích chịu trọng lực, chịu được sức gió, chịu lực tác động từ bên ngoài.Kết cấu chính của nhà xưởng khung thép gồm móng, dầm, kết cấu mang lực mái, dầm cầu chạy, hệ khung chống gió, hệ giằng và cột kèo hình chữ T làm khung chính.Để tính được đơn giá xây dựng nhà xưởng khung thép, cần phải tính toán chi tiết hệ kết cấu chính của xưởng.', '537.png', '1', 1, '1', 1, '2022-03-23 01:47:26', '2022-03-23 01:47:26');
INSERT INTO `posts` (`id`, `title`, `slug_title`, `content`, `summary`, `images`, `hot`, `trending`, `active`, `type_id`, `created_at`, `updated_at`) VALUES
(18, 'Thi Công Nhà Xưởng Khung Thép Tiền Chế Hiện Đại', 'thi-cong-nha-xuong-khung-thep-tien-che-hien-dai', '<h2><span style=\"font-size:18px\"><strong>Th&ocirc;ng số cơ bản của nh&agrave; xưởng khung th&eacute;p tiền chế.</strong></span></h2>\r\n\r\n<p><span style=\"font-size:18px\">C&aacute;c th&ocirc;ng số n&agrave;y gi&uacute;p x&aacute;c định thiết kế, c&aacute;c y&ecirc;u cầu kỹ thuận v&agrave; đơn gi&aacute; x&acirc;y dựng nh&agrave; th&eacute;p tiền chế trong c&aacute;c hợp đồng k&yacute; kế hợp t&aacute;c giữ CĐT v&agrave; c&ocirc;ng ty thi c&ocirc;ng nh&agrave; khung th&eacute;p.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><strong>Chiều rộng nh&agrave; xưởng hay khẩu độ</strong>: Chiều rộng của nh&agrave; t&ugrave;y thuộc v&agrave;o y&ecirc;u cầu. Kh&ocirc;ng hạn chế về chiều rộng nh&agrave;. Chiều rộng nh&agrave; được t&iacute;nh từ m&eacute;p ngo&agrave;i tường đến m&eacute;p ngo&agrave;i tường.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Chiều d&agrave;i nh&agrave;:</strong>&nbsp;Chiều d&agrave;i của nh&agrave; t&ugrave;y thuộc v&agrave;o y&ecirc;u cầu. Kh&ocirc;ng hạn chế về chiều d&agrave;i nh&agrave;. Chiều d&agrave;i nh&agrave; được t&iacute;nh từ m&eacute;p ngo&agrave;i tường đến m&eacute;p ngo&agrave;i tường.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Chiều cao nh&agrave;:</strong>&nbsp;Chiều cao của nh&agrave; t&ugrave;y thuộc v&agrave;o y&ecirc;u cầu. Chiều cao nh&agrave; được t&iacute;nh từ ch&acirc;n cột đến diềm m&aacute;i (giao giữa t&ocirc;n m&aacute;i v&agrave; t&ocirc;n tường).</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Độ dốc m&aacute;i:</strong>&nbsp;Độ dốc m&aacute;i ảnh hưởng đến việc tho&aacute;t nước mưa ở tr&ecirc;n m&aacute;i. Th&ocirc;ng thường, độ dốc m&aacute;i được lấy i = 15%.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Bước cột:</strong>&nbsp;Bước cột l&agrave; khoảng c&aacute;ch giữa c&aacute;c cột theo phương dọc nh&agrave;. Bước cột được x&aacute;c định dựa v&agrave;o chiều d&agrave;i của nh&agrave; v&agrave; mục đ&iacute;ch sử dụng trong nh&agrave;.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Tải trọng:</strong>&nbsp;Tải trọng t&iacute;nh to&aacute;n t&aacute;c động l&ecirc;n c&ocirc;ng tr&igrave;nh bao gồm c&aacute;c loại tải trọng sau: trọng lượng bản th&acirc;n, hoạt tải m&aacute;i, tải trọng gi&oacute;, tải trọng cầu trục tải trọng s&agrave;n (nếu c&oacute;), tải trọng sử dụng.</span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Quy tr&igrave;nh thiết kế v&agrave; thi c&ocirc;ng x&acirc;y dựng nh&agrave; xưởng th&eacute;p tiền chế.</strong></span></h2>\r\n\r\n<p><span style=\"font-size:18px\">Quy tr&igrave;nh thi c&ocirc;ng nh&agrave; th&eacute;p tiền chế l&agrave; c&ocirc;ng việc quan trọng v&agrave; kh&ocirc;ng hề đơn giản. Để c&oacute; được một nền m&oacute;ng tốt đ&ograve;i hỏi đơn vị thi c&ocirc;ng nh&agrave; th&eacute;p phải c&oacute; sự chuẩn bị ngay từ c&ocirc;ng đoạn đầu ti&ecirc;n. Chủ động t&igrave;m hiểu th&ocirc;ng về dự &aacute;n để qu&aacute; tr&igrave;nh thi c&ocirc;ng diễn ra tốt nhất. Quy tr&igrave;nh thi c&ocirc;ng nh&agrave; th&eacute;p tiền chế chuẩn &ndash; chuy&ecirc;n nghiệp trải 3 qua giai đoạn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>=&gt; Thiết kế chi tiết. =&gt; Gia c&ocirc;ng c&aacute;c cấu kiện th&eacute;p. =&gt; V&agrave; thi c&ocirc;ng lắp dựng ho&agrave;n thiện tại c&ocirc;ng trường.</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Kh&aacute;c với x&acirc;y dựng nh&agrave; xưởng b&ecirc; t&ocirc;ng cốt th&eacute;p truyền thống. Quy tr&igrave;nh x&acirc;y dựng nh&agrave; xưởng tiền chế được cải tiến hơn. C&aacute;c c&ocirc;ng đoạn như sản xuất gia c&ocirc;ng khung th&eacute;p nh&agrave; xưởng v&agrave; thi c&ocirc;ng phần m&oacute;ng được tiến h&agrave;nh song song với nhau. Phần m&oacute;ng nh&agrave; xưởng sẽ được thi c&ocirc;ng tại c&ocirc;ng trường trong khi phần kết cấu th&eacute;p sẽ được gia c&ocirc;ng tại nh&agrave; m&aacute;y. Việc đưa một phần lớn c&ocirc;ng việc x&acirc;y dựng nh&agrave; xưởng tiền chế về nh&agrave; m&aacute;y nơi c&oacute; đủ m&aacute;y m&oacute;c hiện đại v&agrave; c&ocirc;ng nh&acirc;n tay nghề cao đ&atilde; gi&uacute;p qu&aacute; tr&igrave;nh thi c&ocirc;ng nh&agrave; xưởng khung th&eacute;p diễn ra nhanh ch&oacute;ng v&agrave; chất lượng cũng cao hơn nhiều.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://vietmysteel.com/wp-content/uploads/2021/08/huong-dan-xin-phep-xay-dung-nha-xuong.jpg\" style=\"height:461px; width:800px\" /></span></p>\r\n\r\n<h2><span style=\"font-size:18px\">Cấu tạo kết cấu nh&agrave; xưởng khung th&eacute;p tiền chế</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">Cấu tạo của một nh&agrave; xưởng khung th&eacute;p tiền chế phổ biến gồm 6 phần ch&iacute;nh:&nbsp;<strong>m&oacute;ng, nền nh&agrave;, hệ khung ch&iacute;nh, c&aacute;c loại của của nh&agrave; xưởng, x&agrave; gồ v&agrave; hệ giằng, t&ocirc;n bao che xung quanh v&agrave; t&ocirc;n m&aacute;i.</strong></span></p>\r\n\r\n<h3><span style=\"font-size:18px\">#1. Kết cấu M&oacute;ng &ndash; Nền nh&agrave; xưởng tiền chế</span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; xưởng tiền chế vẫn sử dụng hệ m&oacute;ng b&ecirc; t&ocirc;ng cốt th&eacute;p chắc chắn. Hệ m&oacute;ng c&oacute; t&aacute;c dụng truyền tải trọng từ tr&ecirc;n xuống nền đất cứng b&ecirc;n dưới. M&oacute;ng c&oacute; nhiều loại c&oacute; thể l&agrave; m&oacute;ng đơn, m&oacute;ng băng, m&oacute;ng b&egrave; hay m&oacute;ng cọc t&ugrave;y v&agrave;o địa chất vị tr&iacute; đ&oacute; v&agrave; tải trọng của c&ocirc;ng tr&igrave;nh.<br />\r\nTrước khi đổ b&ecirc; t&ocirc;ng m&oacute;ng, bulong m&oacute;ng hay c&ograve;n gọi l&agrave; bu l&ocirc;ng neo phải được li&ecirc;n kết ch&iacute;nh x&aacute;c v&agrave; chắn chắn v&agrave;o hệ th&eacute;p m&oacute;ng, Bulong neo thường hay được sử dụng&nbsp;<strong>M24 v&agrave; M27</strong>&nbsp;Lắp đặt bu l&ocirc;ng m&oacute;ng l&agrave; một kh&acirc;u quan trọng v&agrave; y&ecirc;u cầu độ ch&iacute;nh x&aacute;c cao để đảm bảo việc lắp đặt c&aacute;c cấu kiện cột, k&egrave;o th&eacute;p l&agrave; dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Nền nh&agrave; xưởng thường được đổ b&ecirc; t&ocirc;ng dưới l&agrave; lớp base v&agrave; c&aacute;t đầm chặt. Chiều d&agrave;y b&ecirc; t&ocirc;ng của nền phụ thuộc v&agrave;o tải trọng m&aacute;y m&oacute;c v&agrave; xe di chuyển b&ecirc;n trong nh&agrave; xưởng. Mặt nền thường được đ&aacute;nh b&oacute;ng hoặc sơn epoxy để bảo đảm bề mặt b&oacute;ng sạch trong qu&aacute; tr&igrave;nh sử dụng.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\">#2 Khung th&eacute;p nh&agrave; xưởng tiền chế</span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Kết cấu th&eacute;p nh&agrave; xưởng l&agrave; hệ khung kết cấu ch&iacute;nh: Cột, dầm, v&igrave; k&egrave;o th&eacute;p&hellip; N&oacute;i chung&nbsp;<strong>kết cấu th&eacute;p nh&agrave; xưởng</strong>&nbsp;gồm những cấu kiện c&oacute; khả năng chịu được lực lớn từ c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng, đặc biệt n&oacute; được thiết kế v&agrave; cấu tạo ho&agrave;n to&agrave;n bằng th&eacute;p. Kết cấu nh&agrave; xưởng th&eacute;p được ứng dụng rộng r&atilde;i trong ng&agrave;nh x&acirc;y dựng, từ những c&ocirc;ng tr&igrave;nh c&oacute; quy m&ocirc; nhỏ đến những c&ocirc;ng tr&igrave;nh x&acirc;y dựng c&oacute; quy m&ocirc; lớn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Th&ocirc;ng thường, để ho&agrave;n th&agrave;nh n&ecirc;n một kết cấu nh&agrave; xưởng th&eacute;p ho&agrave;n chỉnh sẽ bao gồm:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><strong>Khung ch&iacute;nh:</strong>&nbsp;khung ch&iacute;nh l&agrave; cột v&agrave; k&egrave;o, được cấu kiện từ tổ hợp c&oacute; tiết diện I. K&iacute;ch thước của mỗi cấu kiện sẽ được lựa chọn theo lực lớn nhất của nh&agrave; th&eacute;p.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Khung ngang:</strong>&nbsp;Kết cấu khung ngang được li&ecirc;n hệ với nhau bằng c&aacute;c kết cấu dọc, cụ thể như hệ giằng, kết cấu m&aacute;i, dầm cầu trục, kết cấu dỡ tường.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Khung phụ:</strong>&nbsp;Gồm x&agrave; gồ m&aacute;i, dầm tường v&agrave; thanh chống đỉnh tường. Ch&uacute;ng c&oacute; h&igrave;nh dạng chữ Z v&agrave; được thiết kế nhỏ hơn so với những thanh th&eacute;p chữ C. C&aacute;c cấu kiện h&igrave;nh chữ Z sẽ được chồng l&ecirc;n nhau tạo th&agrave;nh cấu kiện liền, dọc theo chiều d&agrave;i của nh&agrave; xưởng th&eacute;p v&agrave; l&agrave;m tăng khả năng chịu lực.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\">Ngo&agrave;i ra, kết cấu nh&agrave; xưởng th&eacute;p c&ograve;n được cấu tạo từ những tấm th&eacute;p tạo h&igrave;nh bằng c&aacute;n, t&ocirc;n lợp m&aacute;i.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://vietmysteel.com/wp-content/uploads/2021/08/cau-tao-nha-xuong-tien-che.jpg\" style=\"height:451px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cấu tạo nh&agrave; xưởng khung th&eacute;p tiền chế</span></p>\r\n\r\n<h3><span style=\"font-size:18px\">#3. Cửa trời v&agrave; m&aacute;i canopy</span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Cửa trời thường đặt tr&ecirc;n đỉnh của nh&agrave; xưởng, c&oacute; t&aacute;c dụng th&ocirc;ng gi&oacute; gi&uacute;p nh&agrave; xưởng th&ocirc;ng tho&aacute;ng trong qu&aacute; tr&igrave;nh vận h&agrave;nh, sản xuất Canopy l&agrave; hệ m&aacute;i sảnh c&oacute; t&aacute;c dụng che nắng mưa tại vị tr&iacute; cửa đi, cửa sổ của nh&agrave; xưởng</span></p>\r\n\r\n<h3><span style=\"font-size:18px\">#4. X&agrave; gồ v&agrave; hệ giằng m&aacute;i, giằng cột, giằng x&agrave; gồ</span></h3>\r\n\r\n<p><span style=\"font-size:18px\">X&agrave; gồ th&eacute;p mạ kẽm thường c&oacute; dạng chữ C, Z &hellip; khoảng c&aacute;ch x&agrave; gồ khoảng từ 1m &ndash; 1,5m ch&uacute;ng được li&ecirc;n kết với khung ch&iacute;nh c&oacute; t&aacute;c dụng đỡ hệ m&aacute;i t&ocirc;n b&ecirc;n tr&ecirc;n. Hệ giằng m&aacute;i, giằng cột tuy khối lượng kh&ocirc;ng chiếm nhiều nhưng l&agrave; phần kh&ocirc;ng thể thiếu của kết cấu nh&agrave; xưởng. Hệ giằng c&oacute; t&aacute;c dụng tăng sự ổn định của hệ khung kết cấu ch&iacute;nh trong qu&aacute; tr&igrave;nh lắp dựng v&agrave; trong khi sử dụng. Rất nhiều hệ kết cấu nh&agrave; xưởng c&oacute; thể bị biến dạng v&igrave; chủ quan với hệ giằng n&agrave;y.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\">#5. T&ocirc;n bao che, vật liệu c&aacute;ch nhiệt v&agrave; t&ocirc;n m&aacute;i.</span></h3>\r\n\r\n<p><span style=\"font-size:18px\">Đơn giản nhất l&agrave; loại t&ocirc;n 1 lớp mạ m&agrave;u tăng t&iacute;nh thẩm mỹ v&agrave; gi&uacute;p tr&aacute;nh ăn m&ograve;n bởi m&ocirc;i trường. Tuy vậy với thời tiết nắng n&oacute;ng, ẩm thấp như Việt Nam, m&aacute;i t&ocirc;n thường được cấu tạo th&ecirc;m 1 lớp c&aacute;ch nhiệt bằng t&uacute;i kh&iacute; hoặc lớp b&ocirc;ng thủy tinh gi&uacute;p cho nh&agrave; xưởng chống n&oacute;ng v&agrave; chống ồn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://vietmysteel.com/wp-content/uploads/2021/08/thi-cong-lap-dung-ket-cau-thep-nha-xuong-tien-che.jpg\" style=\"height:450px; width:800px\" /></span></p>\r\n\r\n<h2><span style=\"font-size:18px\">B&aacute;o gi&aacute; x&acirc;y dựng nh&agrave; xưởng khung th&eacute;p tiền chế Vietmysteel 2022.</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">Ch&uacute; &yacute;: Đơn gi&aacute; gi&aacute; chỉ mang t&iacute;nh chất tham khảo v&agrave; c&oacute; thể thay đổi t&ugrave;y từng thời điểm v&agrave; gi&aacute; th&eacute;p thế giới. Qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; nhu cầu tư vấn x&acirc;y dựng, lắp dựng nh&agrave; xưởng tiền chế vui l&ograve;ng li&ecirc;n hệ&nbsp;<strong>Vietmysteel</strong>&nbsp;để được tư vấn v&agrave;&nbsp;<strong>b&aacute;o gi&aacute; x&acirc;y dựng nh&agrave; xưởng khung th&eacute;p</strong>&nbsp;nhanh v&agrave; ch&iacute;nh x&aacute;c nhất cho c&ocirc;ng tr&igrave;nh của bạn.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\">B&aacute;o gi&aacute; thi c&ocirc;ng nh&agrave; xưởng khung th&eacute;p tiền chế theo từng hạng mục</span></h3>\r\n\r\n<table align=\"center\" border=\"1\" summary=\"Giá xây dựng nhà xưởng khung thép tiền chế theo từng hạng mục thi công\">\r\n	<caption><span style=\"font-size:18px\"><strong>Gi&aacute; x&acirc;y dựng nh&agrave; xưởng khung th&eacute;p tiền chế theo từng hạng mục thi c&ocirc;ng</strong></span></caption>\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\"><span style=\"font-size:18px\"><strong>HẠNG MỤC GIA C&Ocirc;NG KẾT CẤU TH&Eacute;P</strong></span></th>\r\n			<th scope=\"col\"><span style=\"font-size:18px\"><strong>ĐƠN VỊ</strong></span></th>\r\n			<th scope=\"col\"><span style=\"font-size:18px\"><strong>ĐƠN GI&Aacute; (ĐỒNG)</strong></span></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Khung k&egrave;o th&eacute;p, Dầm vượt nhịp</span></td>\r\n			<td><span style=\"font-size:18px\">Kg</span></td>\r\n			<td><span style=\"font-size:18px\">5.000đ &ndash; 8.000đ/Kg</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Nguy&ecirc;n liệu th&eacute;p tấm kh&aacute;ch h&agrave;ng cung cấp hoặc theo gi&aacute; th&eacute;p hiện h&agrave;nh</span></td>\r\n			<td><span style=\"font-size:18px\">Kg</span></td>\r\n			<td><span style=\"font-size:18px\">Theo gi&aacute; th&eacute;p tr&ecirc;n thị trường (16-18.000đ/Kg)</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\"><strong>Hạng mục thi c&ocirc;ng lắp dựng kết cấu th&eacute;p</strong></span></td>\r\n			<td><span style=\"font-size:18px\"><strong>Đơn vị</strong></span></td>\r\n			<td><span style=\"font-size:18px\"><strong>Đơn gi&aacute; nh&acirc;n c&ocirc;ng theo từng hạng mục</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Lắp dựng khung k&egrave;o ch&iacute;nh</span></td>\r\n			<td><span style=\"font-size:18px\">m<sup>2</sup></span></td>\r\n			<td><span style=\"font-size:18px\">50.000đ/m<sup>2</sup></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Lắp dựng khung n&oacute;c gi&oacute;</span></td>\r\n			<td><span style=\"font-size:18px\">m<sup>2</sup></span></td>\r\n			<td><span style=\"font-size:18px\">50.000đ/m<sup>2</sup></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Lắp dựng consonle cửa đi</span></td>\r\n			<td><span style=\"font-size:18px\">m<sup>2</sup></span></td>\r\n			<td><span style=\"font-size:18px\">50.000đ/m<sup>2</sup></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Lắp đặt t&ocirc;n m&aacute;i &nbsp;(kh&ocirc;ng gồm chống n&oacute;ng )</span></td>\r\n			<td><span style=\"font-size:18px\">m<sup>2</sup></span></td>\r\n			<td><span style=\"font-size:18px\">20.000đ/m<sup>2</sup></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Lắp đạt t&ocirc;n v&aacute;ch</span></td>\r\n			<td><span style=\"font-size:18px\">m<sup>2</sup></span></td>\r\n			<td><span style=\"font-size:18px\">25.000đ/m<sup>2</sup></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Lắp đặt m&aacute;ng xối, ống nước</span></td>\r\n			<td><span style=\"font-size:18px\">m<sup>2</sup></span></td>\r\n			<td><span style=\"font-size:18px\">75.000đ/m<sup>2</sup></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\">Lắp đặt viền (diềm),chỉ (Viền &uacute;p n&oacute;i v&agrave; viềm đầu hồi, viền ch&acirc;n t&ocirc;n&hellip;)</span></td>\r\n			<td><span style=\"font-size:18px\">m<sup>2</sup></span></td>\r\n			<td><span style=\"font-size:18px\">5.000đ/m<sup>2</sup></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3><span style=\"font-size:18px\">B&aacute;o gi&aacute; x&acirc;y dựng nh&agrave; xưởng th&eacute;p tiền chế​ trọn g&oacute;i.</span></h3>\r\n\r\n<h4><span style=\"font-size:18px\"><strong>Đơn gi&aacute; nh&agrave; xưởng th&eacute;p tiền chế &aacute;p dụng cho hệ vượt nhịp từ 20m đến 30m.&nbsp;</strong></span></h4>\r\n\r\n<p><span style=\"font-size:18px\">Gi&aacute; từ&nbsp;<strong>1,410,000đ/m2 &ndash; 2,500,000đ/m2</strong>&nbsp;t&ugrave;y thuộc v&agrave;o diện t&iacute;ch, qui m&ocirc; nh&agrave; xưởng, ng&agrave;nh nghề hoạt động, khung k&egrave;o cột, nền nh&agrave; xưởng. C&ocirc;ng ty đưa ra bảng b&aacute;o gi&aacute; tham khảo mức độ tương đối ch&iacute;nh x&aacute;c, t&ugrave;y v&agrave;o diện t&iacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>M&ocirc; tả sơ bộ:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Nền nh&agrave; xưởng b&ecirc; t&ocirc;ng cốt th&eacute;p, sơn epoxy</span></li>\r\n	<li><span style=\"font-size:18px\">Chiều cao dưới 7,5m;</span></li>\r\n	<li><span style=\"font-size:18px\">Cột, v&igrave; k&egrave;o th&eacute;p tổ hợp.</span></li>\r\n	<li><span style=\"font-size:18px\">M&aacute;i panel d&agrave;y 50mm, tường panel, của nh&ocirc;m k&iacute;nh</span></li>\r\n	<li><span style=\"font-size:18px\">Trần thạch cao</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://vietmysteel.com/wp-content/uploads/2021/08/nha-xuong-tien-che-nhieu-tang.jpg\" style=\"height:450px; width:800px\" /></span></p>\r\n\r\n<h4><span style=\"font-size:18px\">Gi&aacute; nh&agrave; xưởng tiền chế sử dụng cột b&ecirc; t&ocirc;ng cốt th&eacute;p, khung v&igrave; k&egrave;o th&eacute;p tiền chế, m&aacute;i t&ocirc;n.</span></h4>\r\n\r\n<p><span style=\"font-size:18px\">Đơn gi&aacute; x&acirc;y dựng:&nbsp;<strong>2.000.000 đ/m2 đến 2.200.000 đ/m2</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>M&ocirc; tả sơ bộ:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Chiều cao dưới 7,5m,</span></li>\r\n	<li><span style=\"font-size:18px\">Cột b&ecirc; t&ocirc;ng cốt th&eacute;p,</span></li>\r\n	<li><span style=\"font-size:18px\">V&igrave; k&egrave;o th&eacute;p tổ hợp, c&oacute; cửa trời,</span></li>\r\n	<li><span style=\"font-size:18px\">M&aacute;i t&ocirc;n 0,45m</span></li>\r\n	<li><span style=\"font-size:18px\">Tường 220 x&acirc;y cao 4m, thưng t&ocirc;n v&agrave; cửa chớp t&ocirc;n</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://vietmysteel.com/wp-content/uploads/2021/08/thi-cong-nha-xuong-tien-che-vietmysteel.jpg\" style=\"height:450px; width:800px\" /></span></p>\r\n\r\n<h4><span style=\"font-size:18px\"><strong>Đơn gi&aacute; &aacute;p dụng cho nh&agrave; xưởng, nh&agrave; kho đơn giản</strong></span></h4>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Diện t&iacute;ch: dưới 1500m2, cao độ dưới 7,5m, cột x&acirc;y l&otilde;i th&eacute;p hoặc cột đổ b&ecirc; t&ocirc;ng, k&egrave;o th&eacute;p v, sắt hộp, v&aacute;ch x&acirc;y tường 100mm, v&aacute;ch tole,m&aacute;i tole</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>Kh&ocirc;ng cầu trục:</strong>&nbsp;Đơn gi&aacute; x&acirc;y dựng từ&nbsp;<strong>1.300.000 đ/m2 &ndash; 1.500.000 đ/m2.</strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong>C&oacute; cầu trục 5 &ndash; 10 tấn:</strong>&nbsp;Đơn gi&aacute; x&acirc;y dựng từ&nbsp;<strong>1.800.000 đ/m2 &ndash; 2.000.000 đ/m2</strong></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><strong>M&ocirc; tả sơ bộ c&ocirc;ng tr&igrave;nh:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Chiều cao dưới 7.5m,</span></li>\r\n	<li><span style=\"font-size:18px\">Tường 110 x&acirc;y cao 2m, thưng t&ocirc;n, cửa chớp t&ocirc;n</span></li>\r\n	<li><span style=\"font-size:18px\">M&aacute;i t&ocirc;n 1 lớp 0,45mm</span></li>\r\n	<li><span style=\"font-size:18px\">Cột k&egrave;o th&eacute;p tổ hợp</span></li>\r\n	<li><span style=\"font-size:18px\">Nền b&ecirc; t&ocirc;ng d&agrave;y 15cm</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://vietmysteel.com/wp-content/uploads/2021/08/thi-cong-nha-xuong-tien-che-vietmysteel-h2.jpg\" style=\"height:451px; width:800px\" /></span></p>\r\n\r\n<h4><span style=\"font-size:18px\">Đơn gi&aacute; &aacute;p dụng cho nh&agrave; tiền chế, nh&agrave; xưởng, nh&agrave; kho (Kh&ocirc;ng đổ b&ecirc; t&ocirc;ng)</span></h4>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Gi&aacute; từ: 450,000đ/m2 &ndash; 1200,000/m2, x&agrave; gồ C dầy 1,8mm &ndash; 2mm, Sắt hộp 5&times;10, 6&times;12, Cột I100 &ndash; I200 hoặc sử dụng cột điện để gi&aacute;m gi&aacute; th&agrave;nh v&agrave; tăng độ bền sản phẩm, Th&eacute;p đặc l&agrave;m k&egrave;o, bản m&atilde;, Bu L&ocirc;ng, Ốc v&iacute;t li&ecirc;n kết, C&aacute;p căng, M&aacute;i tole d&agrave;y 4,5zem.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Ch&uacute; &yacute;</strong>: Đơn<strong>&nbsp;gi&aacute; x&acirc;y dựng nh&agrave; xưởng tiền chế</strong>&nbsp;tr&ecirc;n đ&acirc;y chỉ tham khảo, nếu c&aacute;c bạn c&oacute; nhu cầu t&igrave;m hiểu đơn gi&aacute; x&acirc;y dựng nh&agrave; xưởng, h&atilde;y li&ecirc;n hệ với Vietmysteel để nhận được b&aacute;o gi&aacute; ch&iacute;nh x&aacute;c nhất.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\">Ch&iacute;nh s&aacute;ch ưu đ&atilde;i d&agrave;nh cho kh&aacute;ch h&agrave;ng x&acirc;y dựng nh&agrave; xưởng trọn g&oacute;i tại Vietmysteel 2022.</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">Thay v&igrave; phải t&igrave;m khắp nơi từng hạng mục thi c&ocirc;ng nh&agrave; xưởng ở nhiều c&ocirc;ng ty tại sao bạn kh&ocirc;ng chọn Vietmysteel l&agrave; nh&agrave; thầu thi c&ocirc;ng trọn g&oacute;i cho dự &aacute;n của bạn?. Thi c&ocirc;ng x&acirc;y dựng nh&agrave; xưởng th&eacute;p tiền chế trọn g&oacute;i của ch&uacute;ng t&ocirc;i bao gồm tất cả c&aacute;c hạng mục m&agrave; bạn đang cần.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><strong>MIỄN PH&Iacute;</strong>&nbsp;xin ph&eacute;p x&acirc;y dựng nh&agrave; xưởng</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>MIỄN PH&Iacute;</strong>&nbsp;Thiết kế nh&agrave; xưởng 100%.</span></li>\r\n	<li><span style=\"font-size:18px\">Thi c&ocirc;ng x&acirc;y dựng lắp dựng nh&agrave; xưởng trọn g&oacute;i.</span></li>\r\n	<li><span style=\"font-size:18px\">Thi c&ocirc;ng hệ thống điện nh&agrave; xưởng (điện chiếu s&aacute;ng, hệ thống điện lạnh, điện cơ, tủ điện&hellip;)</span></li>\r\n	<li><span style=\"font-size:18px\">Thi c&ocirc;ng hệ thống cấp tho&aacute;t nước nh&agrave; xưởng sản xuất.</span></li>\r\n	<li><span style=\"font-size:18px\">Thi c&ocirc;ng lắp đặt trạm biến &aacute;p nh&agrave; xưởng sản xuất.</span></li>\r\n	<li><span style=\"font-size:18px\">Thi c&ocirc;ng hệ thống PCCC nh&agrave; xưởng (hệ thống chữa ch&aacute;y v&aacute;ch tường; hệ thống chữa ch&aacute;y tự động Sprinkler)</span></li>\r\n	<li><span style=\"font-size:18px\">Thi c&ocirc;ng lắp đặt cầu trục nh&agrave; xưởng (từ 2 tấn đến 100 tấn, dầm đ&ocirc;i-dầm đơn)</span></li>\r\n	<li><span style=\"font-size:18px\">Thi c&ocirc;ng hệ thống thang m&aacute;y vận chuyển h&agrave;ng trong nh&agrave; xưởng.</span></li>\r\n	<li><span style=\"font-size:18px\"><strong>MIỄN PH&Iacute;</strong>&nbsp;Ho&agrave;n c&ocirc;ng x&acirc;y dựng nh&agrave; xưởng</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\">Ưu điểm của hợp đồng trọn g&oacute;i, tổng thầu l&agrave; khi c&oacute; c&aacute;c vấn đề ph&aacute;t sinh bạn chỉ cần bốc điện thoại l&ecirc;n v&agrave; gọi cho ch&uacute;ng t&ocirc;i th&igrave; ngay lập tức vấn đề của bạn sẽ được giải quyết. Thay thi bạn phải gọi l&ograve;ng v&ograve;ng từ c&ocirc;ng ty n&agrave;y đến đơn vị kh&aacute;c nhưng kh&ocirc;ng giải quyết được g&igrave;. Hơn nữa chi ph&iacute; đầu tư cũng tiết kiệm hơn so với việc chia nhỏ v&agrave; cho nhiều c&ocirc;ng ty c&ugrave;ng tham gia.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đừng ngại.!. H&atilde;y gọi ngay cho ch&uacute;ng t&ocirc;i để được tư vấn. Đội ngũ kỹ sư của ch&uacute;ng t&ocirc;i rất vui khi nhận được cuộc gọi từ bạn.!.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">C&ocirc;ng ty x&acirc;y dựng nh&agrave; xưởng tiền chế Vietmysteel l&agrave; một trong số những c&ocirc;ng ty chuy&ecirc;n x&acirc;y dựng nh&agrave; xưởng khung th&eacute;p tiền chế uy t&iacute;n tại khu vực ph&iacute;a Nam.&nbsp;<strong>Với hệ thống 3 nh&agrave; m&aacute;y sản xuất kết cấu th&eacute;p lớn tại TP.HCM; B&igrave;nh Dương v&agrave; Long An</strong>. C&ugrave;ng đội ngũ kỹ sư, kiến tr&uacute;c sư v&agrave; đội thợ chuy&ecirc;n thi c&ocirc;ng nh&agrave; tiền chế. Ch&uacute;ng t&ocirc;i đ&atilde; thi c&ocirc;ng ho&agrave;n th&agrave;nh c&ocirc;ng h&agrave;ng ng&agrave;n dự &aacute;n lớn nhỏ. Khi b&agrave;n giao cho kh&aacute;ch h&agrave;ng lu&ocirc;n được đ&aacute;nh gi&aacute; cao về tiến độ thi c&ocirc;ng nhanh ch&oacute;ng v&agrave; chất lượng ổn địn, đặc biệt l&agrave; chế độ bảo h&agrave;nh l&acirc;u d&agrave;i đồng h&agrave;nh với doanh nghiệp ch&iacute;nh l&agrave; lời cam kết về những c&ocirc;ng tr&igrave;nh nh&agrave; th&eacute;p tiền chế m&agrave; ch&uacute;ng t&ocirc;i đ&atilde; x&acirc;y dựng.</span></p>', 'Chú ý: Đơn giá giá chỉ mang tính chất tham khảo và có thể thay đổi tùy từng thời điểm và giá thép thế giới. Quý khách hàng có nhu cầu tư vấn xây dựng, lắp dựng nhà xưởng tiền chế vui lòng liên hệ Vietmysteel để được tư vấn và báo giá xây dựng nhà xưởng khung thép nhanh và chính xác nhất cho công trình của bạn.', '1319.jpg', '1', 1, '1', 2, '2022-03-13 19:30:16', '2022-03-13 19:30:16'),
(19, 'Báo giá thi công làm nhà khung thép mái tôn đẹp giá rẻ mới nhất', 'bao-gia-thi-cong-lam-nha-khung-thep-mai-ton-dep-gia-re-moi-nhat', '<h2><span style=\"font-size:18px\">1. Nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n l&agrave; g&igrave;?</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; th&eacute;p tiền chế hay&nbsp;<a href=\"http://webxaydung.net/bao-gia-lam-mai-ton-gia-re\"><u><strong>nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n</strong></u></a>&nbsp;l&agrave; loại nh&agrave; được x&acirc;y dựng với khung trụ bằng th&eacute;p v&agrave; lắp đặt theo một bản vẽ kiến tr&uacute;c kỹ thuật. C&aacute;c kết cấu th&eacute;p của nh&agrave; tiền chế được sản xuất sẵn sau đ&oacute; được lắp dựng tại c&ocirc;ng trường cho n&ecirc;n thời gian ho&agrave;n th&agrave;nh sản phẩm rất nhanh.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">CTY&nbsp;THƯƠNG MẠI DỊCH VỤ &amp; X&Acirc;Y LẮP DƯƠNG GIA GLASS</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&ndash; Địa chỉ: To&agrave; Dương Gia Group DG5-18 &ndash; KĐT Kiến Hưng &ndash; H&agrave; Đ&ocirc;ng &ndash;&nbsp;HN</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&ndash; Chi Nh&aacute;nh: Số 26/6 Đường 18B &ndash; B&igrave;nh Hưng H&ograve;a A &ndash; B&igrave;nh T&acirc;n &ndash; Tp HCM</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&ndash; Qu&yacute; kh&aacute;ch gọi trực tiếp để được b&aacute;o gi&aacute; v&agrave; tư vấn về nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&ndash;&nbsp;<strong>L&agrave;m m&aacute;i t&ocirc;n tại H&agrave; Nội</strong>&nbsp;v&agrave; khu vực ph&iacute;a bắc:&nbsp;<strong><a href=\"tel:0888.855.913\">0888.855.913</a></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&ndash;&nbsp;<strong>L&agrave;m m&aacute;i t&ocirc;n&nbsp;tại TPHCM</strong>&nbsp;v&agrave; khu vực ph&iacute;a nam:&nbsp;<strong><a href=\"tel:0946.373.885\">0946.373.885</a></strong></span></p>\r\n\r\n<h2><span style=\"font-size:18px\">2. B&aacute;o gi&aacute; thi c&ocirc;ng l&agrave;m nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n gi&aacute; rẻ</span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><u><em>Ghi ch&uacute;</em></u></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><em>Đơn gi&aacute; tr&ecirc;n chưa bao gồm thuế VAT</em></span></li>\r\n	<li><span style=\"font-size:18px\"><em>Đơn gi&aacute; tr&ecirc;n đ&atilde; bao gồm thi c&ocirc;ng lắp đặt ho&agrave;n thiện</em></span></li>\r\n	<li><span style=\"font-size:18px\"><em>Đơn gi&aacute; tr&ecirc;n đ&atilde; bao gồm vận chuyển trong nội th&agrave;nh H&agrave; Nội</em>&nbsp;<em>v&agrave; TPHCM</em></span></li>\r\n	<li><span style=\"font-size:18px\"><em>Sản phẩm thiết kế, b&aacute;o gi&aacute; sẽ được căn cứ v&agrave;o bản vẽ chi tiết theo hồ sơ thiết kế.</em></span></li>\r\n	<li><span style=\"font-size:18px\"><em>Tất cả sản phẩm bảo h&agrave;nh 12 th&aacute;ng</em></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\">3. Một số h&igrave;nh ảnh về khung th&eacute;p m&aacute;i t&ocirc;n, nh&agrave; th&eacute;p tiền chế</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>&nbsp;L&agrave;m nh&agrave; m&aacute;i t&ocirc;n</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">L&agrave;m nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">Thi c&ocirc;ng vỉ k&egrave;o khung th&eacute;p</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">Dựng khung th&eacute;p nh&agrave; m&aacute;i t&ocirc;n</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">Thi c&ocirc;ng m&aacute;i t&ocirc;n chống n&oacute;ng</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">L&agrave;m khung th&eacute;p m&aacute;i t&ocirc;n</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">Lợp nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n trọn g&oacute;i</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em><u>Xem th&ecirc;m</u>:</em></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><strong><a href=\"https://webnoithat.net/bao-gia-cua-cong-hang-rao-lan-can-cau-thang-nhom-duc-dep-gia-re\">Cổng&nbsp;nh&ocirc;m đ&uacute;c đẹp</a></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><a href=\"http://webnoithat.net/bao-gia-cua-luoi-chong-muoi-cua-luoi-chong-con-trung-gia-re\">Cửa lưới chống muỗi</a></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><a href=\"https://webxaydung.net/bao-gia-thi-cong-lam-tran-thach-cao-gia-re\">Trần thạch cao gi&aacute; rẻ</a></strong></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">L&agrave;m khung th&eacute;p m&aacute;i t&ocirc;n nh&agrave; xưởng</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">L&agrave;m nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">Nh&agrave; khung th&eacute;p để xe</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Khung th&eacute;p m&aacute;i t&ocirc;n nh&agrave; xưởng</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Thi c&ocirc;ng nh&agrave; th&eacute;p tiền chế</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Thi c&ocirc;ng nh&agrave; xưởng</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Thợ thi c&ocirc;ng nh&agrave; khung th&eacute;p</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><strong>Thi c&ocirc;ng l&agrave;m khung th&eacute;p m&aacute;i t&ocirc;n</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>L&agrave;m m&aacute;i t&ocirc;n nh&agrave; xưởng</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>L&agrave;m m&aacute;i che để xe</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><u>T&igrave;m hiểu về khung th&eacute;p m&aacute;i t&ocirc;n, nh&agrave; th&eacute;p tiền chế</u></strong></span></p>\r\n\r\n<h2><span style=\"font-size:18px\">4. Cấu tạo nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n, nh&agrave; th&eacute;p tiền chế</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">Cấu tạo nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n bao gồm c&aacute;c th&agrave;nh phần sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Khung ch&iacute;nh (cột, k&egrave;o th&eacute;p).</span></li>\r\n	<li><span style=\"font-size:18px\">Th&agrave;nh phần kết cấu thứ yếu kh&aacute;c: x&agrave; gồ, dầm&hellip;</span></li>\r\n	<li><span style=\"font-size:18px\">Tấm th&eacute;p tạo h&igrave;nh.</span></li>\r\n	<li><span style=\"font-size:18px\"><a href=\"https://chohanghoa.com.vn/mai-ton/\">T&ocirc;n lợp</a>.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><a href=\"https://chohanghoa.com.vn/bao-gia-thi-cong-mai-ton-cua-cong-sat-hang-rao-sat-lan-can-sat/\"><img alt=\"\" src=\"https://chohanghoa.com.vn/wp-content/uploads/2021/01/bao-gia-chohanghoa.com_.vn_.png\" style=\"height:69px; width:300px\" /></a></span></p>\r\n\r\n<h2><span style=\"font-size:18px\">5. Ưu điểm của nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n nh&agrave; th&eacute;p tiền chế</span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">C&oacute; khả năng chịu lực lớn v&agrave; độ tin cậy cao.</span></li>\r\n	<li><span style=\"font-size:18px\"><a href=\"https://chohanghoa.com.vn/\">Nh&agrave; khung th&eacute;p</a>&nbsp;t&iacute;nh c&ocirc;ng nghiệp h&oacute;a cao.</span></li>\r\n	<li><span style=\"font-size:18px\">Trọng lượng nhẹ so với c&aacute;c vật liệu kh&aacute;c gi&uacute;p l&agrave;m giảm &aacute;p lực tải trọng.</span></li>\r\n	<li><span style=\"font-size:18px\">Tiết kiệm nguy&ecirc;n vật liệu phụ (so với c&aacute;c loại nh&agrave; cố định truyền thống).</span></li>\r\n	<li><span style=\"font-size:18px\">T&iacute;nh cơ động trong vận chuyển, lắp r&aacute;p.</span></li>\r\n	<li><span style=\"font-size:18px\">Lắp dựng đơn giản, nhanh ch&oacute;ng, bất chấp mọi điều kiện thời tiết.</span></li>\r\n	<li><span style=\"font-size:18px\">Tận dụng tối đa diện t&iacute;ch, kh&ocirc;ng gian nh&agrave; xưởng, nh&agrave; m&aacute;y.</span></li>\r\n	<li><span style=\"font-size:18px\">T&iacute;nh đồng bộ cao.</span></li>\r\n	<li><span style=\"font-size:18px\">Dễ mở rộng quy m&ocirc;.</span></li>\r\n	<li><span style=\"font-size:18px\">Tiết kiệm chi ch&iacute;, thời gian thi c&ocirc;ng nhanh</span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\">6. Ứng dụng của nh&agrave; khung th&eacute;p m&aacute;i t&ocirc;n, nh&agrave; th&eacute;p tiền chế</span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Th&iacute;ch hợp cho c&aacute;c c&ocirc;ng tr&igrave;nh lớn, đ&ograve;i hỏi độ bền cao như: Nh&agrave; c&ocirc;ng nghiệp, nh&agrave; xưởng, nh&agrave; m&aacute;y, x&iacute; nghiệp,&hellip;</span></li>\r\n	<li><span style=\"font-size:18px\">Ng&agrave;y nay, c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng trong c&ocirc;ng nghiệp đều d&ugrave;ng&nbsp;<a href=\"http://webnoithat.net/bao-gia-thi-cong-mai-ton-gia-re\"><u><strong>nh&agrave; th&eacute;p tiền chế</strong></u></a>. Ứng dụng của nh&agrave; th&eacute;p tiền chế rất đa dạng, từ nh&agrave; để xe đến c&aacute;c trung t&acirc;m thương mại.</span></li>\r\n	<li><span style=\"font-size:18px\">Những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c n&ecirc;n sử dụng loại vật liệu n&agrave;y: Nh&agrave; m&aacute;y, nh&agrave; xưởng, c&ocirc;ng tr&igrave;nh thương mại nh&agrave; cao tầng,&hellip;</span></li>\r\n</ul>', 'Ngày nay, các công trình xây dựng trong công nghiệp đều dùng nhà thép tiền chế. Ứng dụng của nhà thép tiền chế rất đa dạng, từ nhà để xe đến các trung tâm thương mại.Những công trình kiến trúc nên sử dụng loại vật liệu này: Nhà máy, nhà xưởng, công trình thương mại nhà cao tầng,…', '1647.jpg', '1', 1, '1', 4, '2022-03-15 18:48:09', '2022-03-15 18:48:09');
INSERT INTO `posts` (`id`, `title`, `slug_title`, `content`, `summary`, `images`, `hot`, `trending`, `active`, `type_id`, `created_at`, `updated_at`) VALUES
(20, 'Nhà khung thép công nghệ châu âu.', 'nha-khung-thep-cong-nghe-chau-au', '<p style=\"text-align:justify\"><span style=\"font-size:18px\">Hiện nay, nh&agrave; khung th&eacute;p&nbsp;hay c&ograve;n được gọi l&agrave;&nbsp;<a href=\"http://sanpanelsieunhe.com/san-pham/nha-khung-thep/\">nh&agrave; tiền chế</a>&nbsp;đang được thi c&ocirc;ng rộng khắp cả nước v&agrave; đ&atilde; kh&ocirc;ng c&ograve;n qu&aacute; xa lạ với ch&uacute;ng ta v&igrave; t&iacute;nh hiệu quả của c&ocirc;ng tr&igrave;nh mang lại. Hiện tại&nbsp;<a href=\"http://sanpanelsieunhe.com/san-pham/nha-khung-thep/\">nh&agrave; khung th&eacute;p</a>&nbsp;đang được sử dụng rất nhiều để l&agrave;m nh&agrave; xưởng, nh&agrave; kho, si&ecirc;u thị, nh&agrave; điều h&agrave;nh, bệnh viện, kho h&agrave;ng bảo quản, nh&agrave; giữ xe, thậm ch&iacute; nh&agrave; khung th&eacute;p c&ograve;n c&oacute; thể thiết kế để trở th&agrave;nh nh&agrave; cao tầng sử dụng như nh&agrave; ở.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nh&agrave; khung th&eacute;p</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>Lợi &iacute;ch từ việc thi c&ocirc;ng v&agrave; sử dụng nh&agrave; khung th&eacute;p?</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Lợi &iacute;ch khi thi c&ocirc;ng nh&agrave; khung th&eacute;p.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; khung th&eacute;p được tạo ra với mục ti&ecirc;u tiết kiệm tối đa chi ph&iacute; x&acirc;y dựng nhưng vẫn đảm bảo y&ecirc;u cầu về độ cứng lẫn độ v&agrave; độ an to&agrave;n cũng như l&agrave; khả năng c&aacute;ch nhiệt tốt của c&ocirc;ng tr&igrave;nh</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ngo&agrave;i ra, việc thi c&ocirc;ng cũng rất nhanh ch&oacute;ng, ch&iacute;nh x&aacute;c do đ&atilde; được thiết kế sẵn v&agrave; chỉ việc lắp dựng tại nơi cần thi c&ocirc;ng. Hơn nữa, nh&agrave; khung th&eacute;p sau qu&aacute; tr&igrave;nh sử dụng c&oacute; thể th&aacute;o ra v&agrave; c&oacute; thể di chuyển lắp đặt tại v&iacute; tr&iacute; mới nhanh ch&oacute;ng, dễ d&agrave;ng. Ch&iacute;nh v&igrave; vậy, nh&agrave; khung th&eacute;p đang dần trở th&agrave;nh một sự lựa chọn tối ưu cho c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng tại Việt Nam hiện nay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; Khung th&eacute;p&nbsp;được lắp v&agrave; dựng bằng những tấm panel t&ocirc;n xốp c&oacute; đặc t&iacute;nh c&aacute;ch &acirc;m v&agrave; c&aacute;ch nhiệt tốt. Quy tr&igrave;nh thực hiện nh&agrave; khung th&eacute;p được lắp v&agrave; dựng một c&aacute;ch đồng bộ dựa tr&ecirc;n c&aacute;c kết cấu th&eacute;p được thiết kế sẵn tổ hợp v&agrave; gia c&ocirc;ng sẵn theo bản thiết kế chỉ định sẵn từ trong nh&agrave; m&aacute;y. Sau đ&oacute; c&aacute;c cấu kiện đ&atilde; th&agrave;nh phẩm đ&oacute; mới được vận chuyển đến c&ocirc;ng trường cần thi c&ocirc;ng để lắp dựng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>C&aacute;c bước thi c&ocirc;ng.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&aacute;c bước thi c&ocirc;ng nh&agrave; khung th&eacute;p</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><em><strong>Lắp dựng tr&ecirc;n nền b&ecirc; t&ocirc;ng:</strong></em></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Bước 1: Tạo nền b&ecirc; t&ocirc;ng phẳng, chiều d&agrave;y từ 80mm đến 100mm.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Bước 2: Lắp dựng nh&agrave; vật liệu nhẹ, tuỳ theo mặt bằng lắp dựng c&oacute; thể linh hoạt lắp dựng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Bước 3: Ho&agrave;n thiện nh&agrave; khung th&eacute;p, lắp đặt hệ thống điện, nước,&hellip;</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><em><strong>Lắp tr&ecirc;n nền đất hoặc nền c&aacute;t:</strong></em></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Bước 1: Định vị c&aacute;c vị tr&iacute; khung trụ, khoan xuống nền đất. Sau đ&oacute; sẽ li&ecirc;n kết hệ khung dầm v&agrave; đặt tấm s&agrave;n cho nh&agrave; vật liệu nhẹ.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Bước 2: Lắp dựng nh&agrave; vật liệu nhẹ, linh hoạt lắp dựng t&ugrave;y theo mặt bằng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Bước 3: Ho&agrave;n thiện nh&agrave; khung th&eacute;p (bao gồm lắp đặt c&aacute;c h&ecirc; thống điện, nước, th&ocirc;ng gi&oacute;&hellip;)</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>Cấu tạo ch&iacute;nh của nh&agrave; khung th&eacute;p:</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Kết cấu ch&iacute;nh của nh&agrave; khung th&eacute;p</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">M&oacute;ng nh&agrave;: Đổ m&oacute;ng cốc tại c&aacute;c vị tr&iacute; ch&acirc;n cột. Đổ giằng b&ecirc; t&ocirc;ng cốt th&eacute;p k&iacute;ch thước 300x300mm theo chu vi nh&agrave;.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Cột nh&agrave; khung th&eacute;p: được l&agrave;m bằng th&eacute;p C theo mẫu đặt h&agrave;ng ri&ecirc;ng cho nh&agrave; lắp gh&eacute;p để lắp tấm t&ocirc;n xốp.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">V&igrave; k&egrave;o nh&agrave; khung th&eacute;p: Sử dụng&nbsp; th&eacute;p hộp,V&nbsp; được li&ecirc;n kết với cột bằng bulong bản m&atilde;.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">X&agrave; gồ m&aacute;i nh&agrave; khung th&eacute;p: d&ugrave;ng th&eacute;p hộp mạ kẽm.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Phần nh&agrave; khung th&eacute;p được sản xuất ra trước tại nh&agrave; m&aacute;y v&agrave; sau đ&oacute; sẽ được chia nhỏ th&agrave;nh nhiều phần với c&aacute;c cấu kiện thuận lợi hơn cho việc vận chuyển lắp r&aacute;p tại c&ocirc;ng tr&igrave;nh. Nếu địa h&igrave;nh m&agrave; kh&ocirc;ng cho ph&eacute;p th&igrave; c&oacute; thể vận chuyển vật liệu theo c&aacute;ch thủ c&ocirc;ng v&agrave; chế tạo tại c&ocirc;ng tr&igrave;nh.&nbsp;Nh&agrave; khung th&eacute;p được lắp gh&eacute;p v&agrave; sơn chống gỉ, kh&aacute;ch h&agrave;ng c&oacute; thể sơn m&agrave;u hoặc mạ kẽm lạnh, điện ph&acirc;n hoặc mạ nh&uacute;ng n&oacute;ng t&ugrave;y y&ecirc;u cầu của kh&aacute;ch h&agrave;ng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">M&aacute;i nh&agrave; khung th&eacute;p: sử dụng T&ocirc;n lợp m&aacute;i (T&ocirc;n + PU + PP) hoặc (T&ocirc;n + xốp + T&ocirc;n), c&aacute;ch &acirc;m, c&aacute;ch nhiệt.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; khung th&eacute;p c&oacute; thể d&ugrave;ng tấm s&agrave;n b&ecirc; t&ocirc;ng cốt th&eacute;p. Hoặc c&oacute; thể d&ugrave;ng c&aacute;c tấm s&agrave;n ceamboad, c&aacute;c tấm v&aacute;n gỗ c&ocirc;ng nghiệp. Mặt s&agrave;n khi được ho&agrave;n thiện c&oacute; thể d&ugrave;ng c&aacute;c tấm thảm trải s&agrave;n pvc, s&agrave;n cao su v&agrave; s&agrave;n gỗ c&ocirc;ng nhiệp&hellip;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Hệ thống cầu thang: cầu thang th&eacute;p, gỗ khi kết nối hai tầng nh&agrave; đảm bảo di chuyển thuận tiện.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">V&aacute;ch bao che xung quanh v&agrave; v&aacute;ch ngăn ph&ograve;ng của nh&agrave; lắp gh&eacute;p : sử dụng&nbsp; (t&ocirc;n + xốp + t&ocirc;n) c&aacute;ch &acirc;m, c&aacute;ch nhiệt. Chiều d&agrave;y sản phẩm&nbsp; 50mm đến 100mm.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Cửa đi, cửa sổ nh&agrave; lắp gh&eacute;p: Cửa nh&ocirc;m k&iacute;nh hoặc cửa nhựa l&otilde;i th&eacute;p gia cường</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Trần nh&agrave; khung th&eacute;p: sử dụng trần nhựa hoặc trần thạch cao t&ugrave;y theo y&ecirc;u cầu của kh&aacute;ch h&agrave;ng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Bu l&ocirc;ng li&ecirc;n kết với dầm m&oacute;ng &ndash; khung nh&agrave; &ndash; diềm m&aacute;i, ốp n&oacute;c, ống tho&aacute;t nước mưa, ống nhựa PVC D76.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>Chi Ph&iacute; để x&acirc;y dựng nh&agrave; khung th&eacute;p.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Chi Ph&iacute; Thi C&ocirc;ng Nh&agrave; Khung Th&eacute;p</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Tr&ecirc;n thực tế nh&agrave; khung th&eacute;p c&oacute; gi&aacute; th&agrave;nh cao nhưng do giảm thiểu thời gian thi c&ocirc;ng, c&ocirc;ng tr&igrave;nh nhanh ch&oacute;ng được đưa v&agrave;o hoạt động, l&agrave;m giảm chi ph&iacute; nh&acirc;n c&ocirc;ng x&acirc;y dựng v&agrave; quản l&yacute; dự &aacute;n &hellip; dẫn đến tiết kiệm lớn chi ph&iacute; cho dự &aacute;n.</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Đơn gi&aacute; tr&ecirc;n thị trường x&acirc;y dựng nh&agrave; khung th&eacute;p hiện nay:<br />\r\n	1.600.000đồng/m2 &ndash;&nbsp; 2.400.000đồng/m2</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Chi ph&iacute; để x&acirc;y dựng nh&agrave; khung th&eacute;p sẽ được t&iacute;nh to&aacute;n dựa tr&ecirc;n quy m&ocirc; v&agrave; mục đ&iacute;ch sử dụng của người sử dụng, chi ph&iacute; hoạch định sẽ được t&iacute;nh theo c&ocirc;ng thức chung như sau:<br />\r\n	Chi Ph&iacute; = Đơn gi&aacute; x kg</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>Những Ưu điểm của nh&agrave; khung th&eacute;p:</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ưu điểm nh&agrave; khung th&eacute;p</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Gi&aacute; thi c&ocirc;ng nh&agrave; lắp gh&eacute;p chỉ bằng 1 nửa so với gi&aacute; nh&agrave; truyền thống được x&acirc;y dựng bằng c&aacute;c chất liệu th&ocirc;ng thường.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; khung th&eacute;p được c&aacute;ch &acirc;m, c&aacute;ch nhiệt tốt.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Di chuyển nh&agrave; khung th&eacute;p đến vị tr&iacute; kh&aacute;c dễ d&agrave;ng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; khung th&eacute;p c&oacute; kết cấu ki&ecirc;n cố, dễ d&agrave;ng th&aacute;o dỡ thuận tiện, trang tr&iacute; đẹp.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Hệ thống kết cấu mềm bằng th&eacute;p nhẹ sẽ an to&agrave;n, đ&aacute;ng tin cậy v&agrave; ph&ugrave; hợp với những y&ecirc;u cầu thiết kế chuẩn của kết cấu x&acirc;y dựng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; khung th&eacute;p c&oacute; th&aacute;o dỡ v&agrave; sử dụng nhiều lần v&agrave; qu&aacute; tr&igrave;nh lắp đặt chỉ đ&ograve;i hỏi những c&ocirc;ng đoạn qu&aacute; phức tạp.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Chỉnh thể nh&agrave; lắp gh&eacute;p nh&igrave;n đẹp mắt, m&agrave;u sắc nh&atilde; nhặn, tạo cảm gi&aacute;c dễ chịu, m&aacute;i nh&agrave; vu&ocirc;ng, phẳng v&agrave; dễ d&agrave;ng cho việc trang tr&iacute;.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Khả năng vượt những nhịp lớn: C&oacute; thể vượt nhịp tối đa 90m với khung loại 1 nhịp v&agrave; tối đa 70m với mỗi module trong nhịp &ndash; với số module l&ecirc;n tới 4 (tổng cộng 280m). Đ&oacute; l&agrave; điều khung b&ecirc; t&ocirc;ng cốt th&eacute;p kh&oacute; m&agrave; l&agrave;m được.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Tối ưu khả năng chịu lực:nh&agrave; khung th&eacute;p đ&atilde; được thiết kế v&agrave; t&iacute;nh to&aacute;n rất ch&iacute;nh x&aacute;c, những bộ phận chịu lực &iacute;t được giảm th&eacute;p đến tối đa v&igrave; thế khung nhẹ hơn v&agrave; tiết kiệm triệt để vật liệu.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Thi c&ocirc;ng nhanh: C&aacute;c chi tiết kết cấu đều được chế tạo sẵn ở nh&agrave; xưởng v&agrave; chỉ đem lắp gh&eacute;p v&igrave; thế đẩy nhanh tiến độ thi c&ocirc;ng cũng như chất lượng của c&ocirc;ng tr&igrave;nh, giảm c&aacute;c ảnh hưởng của thời tiết l&ecirc;n qu&aacute; tr&igrave;nh x&acirc;y dựng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Thi c&ocirc;ng c&ocirc;ng tr&igrave;nh với quy m&ocirc; lớn:c&oacute; tể được sử dụng với c&aacute;c c&ocirc;ng tr&igrave;nh c&oacute; chiều cao tới 30m (t&iacute;nh đến m&eacute;p dưới m&aacute;i) cho nh&agrave; c&oacute; chiều rộng 60m. Việc sử dụng cho c&ocirc;ng tr&igrave;nh với quy m&ocirc; lớn sẽ tiết kiệm được rất nhiều so với sử dụng khung b&ecirc; t&ocirc;ng cốt th&eacute;p.</span></li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:18px\">Những lưu &yacute; khi thi c&ocirc;ng nh&agrave; khung th&eacute;p để đảm bảo an to&agrave;n khi lắp dựng.</span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Lưu &yacute; khi thi c&ocirc;ng nh&agrave; khung th&eacute;p.</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Đảm bảo l&aacute;i cẩu lu&ocirc;n tỉnh t&aacute;o v&agrave; tập trung khi l&agrave;m việc, v&igrave; chỉ cần một sai s&oacute;t nho nhỏ của l&aacute;i cẩu cũng c&oacute; thể ảnh hưởng đến sự an to&agrave;n của con người v&agrave; kết cấu đang x&acirc;y dựng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Tạm dừng thi c&ocirc;ng khi thời tiết gi&oacute; b&atilde;o to.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Để việc thi c&ocirc;ng được an to&agrave;n v&agrave; tối ưu nhất th&igrave; bước đầu cần lắp dựng khoang giằng cứng ở đầu hồi, phải lắp đủ cột, k&egrave;o, x&agrave; gồ, giằng m&aacute;i, giằng tường, tay chống x&agrave; gồ v&agrave; căng một số giằng c&aacute;p tạm treo từ k&egrave;o xuống c&aacute;c cọc neo dưới đất.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\">Sau khi đ&atilde; lắp xong khoang giằng cứng, cần kiểm tra v&agrave; căn chỉnh độ ch&iacute;nh x&aacute;c của khung như: cao độ, độ v&otilde;ng, độ lệch cho ph&eacute;p&hellip; Từ đ&oacute; triển khai lắp khung thiếp theo nối tiếng v&agrave;o khung giằng ch&iacute;nh. Nếu c&oacute; thể lắp đủ số lượng x&agrave; gồ l&agrave; tốt nhất, nếu kh&ocirc;ng th&igrave; lắp &iacute;t nhất 50% số lượng x&agrave; gồ của từng khoang, dần dần ho&agrave;n thiện đến to&agrave;n bộ khung.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ch&uacute;ng t&ocirc;i với đội ngũ kỹ thuật c&oacute; nhiều năm kinh nghiệm trong lĩnh vực x&acirc;y dựng v&agrave; thi c&ocirc;ng nh&agrave; khung th&eacute;p, c&ugrave;ng c&aacute;c bộ phận kỹ thuật, thiết kế ra những sản phẩm nh&agrave; khung th&eacute;p theo ti&ecirc;u chuẩn quốc tế. Ch&uacute;ng t&ocirc;i lu&ocirc;n đảm bảo với qu&yacute; kh&aacute;ch h&agrave;ng về chất lượng khi sản xuất v&agrave; thiết bị triển khai tối ưu đem lại sự chuẩn &nbsp;x&aacute;c nhất cho c&ocirc;ng tr&igrave;nh. H&atilde;y để sản phẩm&nbsp;của&nbsp;<strong>sanpanelsieunhe.com</strong>&nbsp;đồng h&agrave;nh c&ugrave;ng qu&yacute; kh&aacute;ch h&agrave;ng trong sự ph&aacute;t triển thịnh vượng!</span></p>', 'Trên thực tế nhà khung thép có giá thành cao nhưng do giảm thiểu thời gian thi công, công trình nhanh chóng được đưa vào hoạt động, làm giảm chi phí nhân công xây dựng và quản lý dự án … dẫn đến tiết kiệm lớn chi phí cho dự án.', '172.jpg', '1', 1, '1', 3, '2022-03-15 18:55:54', '2022-03-15 18:55:54'),
